// ==================================================
// ENHANCED AUTHENTICATION IMPLEMENTATION
// ==================================================

// lib/auth.ts - Complete authentication system
import { NextRequest } from 'next/server'
import { db } from '@/lib/db'
import crypto from 'crypto'
import bcrypt from 'bcryptjs'
import jwt from 'jsonwebtoken'

const JWT_SECRET = process.env.JWT_SECRET || 'fallback-secret-change-in-production'
const SESSION_DURATION = 7 * 24 * 60 * 60 * 1000 // 7 days
const API_KEY_EXPIRY = 365 * 24 * 60 * 60 * 1000 // 1 year

export interface AuthUser {
  id: string
  email: string
  name?: string
  plan: string
}

export interface AuthResult {
  user: AuthUser
  method: 'session' | 'api_key' | 'jwt'
}

// Password utilities
export async function hashPassword(password: string): Promise<string> {
  const saltRounds = 12 // Higher than default for better security
  return await bcrypt.hash(password, saltRounds)
}

export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  return await bcrypt.compare(password, hash)
}

// Session token generation
export function generateSessionToken(): string {
  return crypto.randomBytes(32).toString('hex')
}

// API Key generation with secure defaults
export function generateApiKey(): { key: string; hash: string; preview: string } {
  const key = `sk_${crypto.randomBytes(24).toString('hex')}`
  const hash = crypto.createHash('sha256').update(key).digest('hex')
  const preview = key.substring(0, 8) + '...'

  return { key, hash, preview }
}

export function hashApiKey(key: string): string {
  return crypto.createHash('sha256').update(key).digest('hex')
}

// JWT utilities
export function generateJWT(userId: string, email: string): string {
  return jwt.sign(
    { 
      userId, 
      email, 
      type: 'access_token',
      iat: Math.floor(Date.now() / 1000)
    },
    JWT_SECRET,
    { 
      expiresIn: '7d',
      issuer: 'ai-sheets',
      audience: 'ai-sheets-users'
    }
  )
}

export function verifyJWT(token: string): { userId: string; email: string } | null {
  try {
    const payload = jwt.verify(token, JWT_SECRET, {
      issuer: 'ai-sheets',
      audience: 'ai-sheets-users'
    }) as any

    return {
      userId: payload.userId,
      email: payload.email
    }
  } catch (error) {
    return null
  }
}

// User creation with proper password hashing
export async function createUser(email: string, password: string, name?: string): Promise<AuthUser> {
  // Check if user already exists
  const existingUser = await db.user.findUnique({
    where: { email }
  })

  if (existingUser) {
    throw new Error('User already exists')
  }

  // Hash password and create user
  const passwordHash = await hashPassword(password)

  const user = await db.user.create({
    data: {
      email,
      name,
      passwordHash, // This field needs to be added to Prisma schema
      plan: 'starter',
      // Give new users 10 free credits to test
      creditLedger: {
        create: {
          delta: 10,
          reason: 'signup_bonus',
          balance: 10,
          metadata: JSON.stringify({ source: 'new_user_bonus' })
        }
      }
    }
  })

  return {
    id: user.id,
    email: user.email,
    name: user.name || undefined,
    plan: user.plan
  }
}

// Login with email and password
export async function loginUser(email: string, password: string): Promise<{ user: AuthUser; sessionToken: string; jwt: string }> {
  const user = await db.user.findUnique({
    where: { email }
  })

  if (!user || !user.passwordHash) {
    throw new Error('Invalid credentials')
  }

  const isValidPassword = await verifyPassword(password, user.passwordHash)
  if (!isValidPassword) {
    throw new Error('Invalid credentials')
  }

  if (!user.isActive) {
    throw new Error('Account is deactivated')
  }

  // Create session
  const sessionToken = generateSessionToken()
  const session = await db.session.create({
    data: {
      userId: user.id,
      token: sessionToken,
      expiresAt: new Date(Date.now() + SESSION_DURATION),
      lastUsed: new Date()
    }
  })

  // Generate JWT
  const jwtToken = generateJWT(user.id, user.email)

  return {
    user: {
      id: user.id,
      email: user.email,
      name: user.name || undefined,
      plan: user.plan
    },
    sessionToken,
    jwt: jwtToken
  }
}

// Comprehensive authentication function
export async function authenticateUser(request: NextRequest): Promise<AuthResult | null> {
  // Try Bearer token first (for API requests)
  const authHeader = request.headers.get('authorization')
  if (authHeader?.startsWith('Bearer ')) {
    const token = authHeader.substring(7)

    // Check if it's a session token
    const session = await db.session.findUnique({
      where: { token },
      include: { user: true }
    })

    if (session && session.expiresAt > new Date() && session.user.isActive) {
      // Update last used timestamp
      await db.session.update({
        where: { id: session.id },
        data: { lastUsed: new Date() }
      })

      return {
        user: {
          id: session.user.id,
          email: session.user.email,
          name: session.user.name || undefined,
          plan: session.user.plan
        },
        method: 'session'
      }
    }

    // Check if it's an API key
    const apiKeyHash = hashApiKey(token)
    const apiKey = await db.apiKey.findUnique({
      where: { keyHash: apiKeyHash },
      include: { user: true }
    })

    if (apiKey && apiKey.isActive && apiKey.user.isActive) {
      // Check if API key is expired
      if (apiKey.expiresAt && apiKey.expiresAt < new Date()) {
        return null
      }

      // Update last used timestamp
      await db.apiKey.update({
        where: { id: apiKey.id },
        data: { lastUsed: new Date() }
      })

      return {
        user: {
          id: apiKey.user.id,
          email: apiKey.user.email,
          name: apiKey.user.name || undefined,
          plan: apiKey.user.plan
        },
        method: 'api_key'
      }
    }

    // Try as JWT token
    const jwtPayload = verifyJWT(token)
    if (jwtPayload) {
      const user = await db.user.findUnique({
        where: { id: jwtPayload.userId }
      })

      if (user && user.isActive) {
        return {
          user: {
            id: user.id,
            email: user.email,
            name: user.name || undefined,
            plan: user.plan
          },
          method: 'jwt'
        }
      }
    }
  }

  // Try JWT cookie (for web app)
  const cookies = request.cookies
  const jwtCookie = cookies.get('auth_token')?.value

  if (jwtCookie) {
    const jwtPayload = verifyJWT(jwtCookie)
    if (jwtPayload) {
      const user = await db.user.findUnique({
        where: { id: jwtPayload.userId }
      })

      if (user && user.isActive) {
        return {
          user: {
            id: user.id,
            email: user.email,
            name: user.name || undefined,
            plan: user.plan
          },
          method: 'jwt'
        }
      }
    }
  }

  return null
}

// API key management
export async function createApiKey(userId: string, name: string): Promise<{ id: string; key: string; preview: string }> {
  const { key, hash, preview } = generateApiKey()

  const apiKey = await db.apiKey.create({
    data: {
      userId,
      name,
      keyHash: hash,
      keyPreview: preview,
      expiresAt: new Date(Date.now() + API_KEY_EXPIRY),
      isActive: true,
      rateLimit: 1000 // Default: 1000 requests per hour
    }
  })

  return {
    id: apiKey.id,
    key, // Only return the actual key once at creation
    preview
  }
}

export async function revokeApiKey(userId: string, keyId: string): Promise<void> {
  await db.apiKey.updateMany({
    where: {
      id: keyId,
      userId
    },
    data: {
      isActive: false
    }
  })
}

// Session cleanup (run periodically)
export async function cleanupExpiredSessions(): Promise<number> {
  const result = await db.session.deleteMany({
    where: {
      expiresAt: {
        lt: new Date()
      }
    }
  })

  return result.count
}

