// RATE LIMITING MIDDLEWARE
// ==================================================

// lib/rate-limit.ts - Advanced rate limiting system
import { NextRequest, NextResponse } from 'next/server'

interface RateLimitConfig {
  windowMs: number        // Time window in milliseconds
  maxRequests: number     // Max requests per window
  keyGenerator?: (req: NextRequest) => string | Promise<string>
  skipSuccessfulRequests?: boolean
  skipFailedRequests?: boolean
  onLimitReached?: (req: NextRequest) => void
}

interface RateLimitEntry {
  count: number
  resetTime: number
  blocked?: boolean
}

// In-memory store (use Redis in production)
const rateLimitStore = new Map<string, RateLimitEntry>()

// Cleanup expired entries periodically
setInterval(() => {
  const now = Date.now()
  for (const [key, entry] of rateLimitStore.entries()) {
    if (entry.resetTime < now) {
      rateLimitStore.delete(key)
    }
  }
}, 60000) // Clean up every minute

export function createRateLimiter(config: RateLimitConfig) {
  return async (request: NextRequest): Promise<NextResponse | null> => {
    const now = Date.now()
    const key = config.keyGenerator 
      ? await config.keyGenerator(request)
      : getDefaultKey(request)

    let entry = rateLimitStore.get(key)

    // Initialize or reset if window expired
    if (!entry || entry.resetTime < now) {
      entry = {
        count: 0,
        resetTime: now + config.windowMs,
        blocked: false
      }
    }

    entry.count++
    rateLimitStore.set(key, entry)

    // Check if limit exceeded
    if (entry.count > config.maxRequests) {
      entry.blocked = true

      if (config.onLimitReached) {
        config.onLimitReached(request)
      }

      // Return rate limit error
      return NextResponse.json(
        {
          error: 'Too Many Requests',
          message: `Rate limit exceeded. Try again in ${Math.ceil((entry.resetTime - now) / 1000)} seconds.`,
          retryAfter: Math.ceil((entry.resetTime - now) / 1000)
        },
        { 
          status: 429,
          headers: {
            'X-RateLimit-Limit': config.maxRequests.toString(),
            'X-RateLimit-Remaining': '0',
            'X-RateLimit-Reset': Math.ceil(entry.resetTime / 1000).toString(),
            'Retry-After': Math.ceil((entry.resetTime - now) / 1000).toString()
          }
        }
      )
    }

    // Add rate limit headers to successful requests
    const response = NextResponse.next()
    response.headers.set('X-RateLimit-Limit', config.maxRequests.toString())
    response.headers.set('X-RateLimit-Remaining', (config.maxRequests - entry.count).toString())
    response.headers.set('X-RateLimit-Reset', Math.ceil(entry.resetTime / 1000).toString())

    return null // Continue to next middleware/handler
  }
}

function getDefaultKey(request: NextRequest): string {
  // Use IP address as default key
  const forwarded = request.headers.get('x-forwarded-for')
  const ip = forwarded ? forwarded.split(',')[0].trim() : 
             request.headers.get('x-real-ip') ||
             request.ip ||
             'unknown'

  return `ip:${ip}`
}

// User-specific rate limiter
export function createUserRateLimiter(config: RateLimitConfig) {
  return createRateLimiter({
    ...config,
    keyGenerator: async (req) => {
      const auth = await authenticateUser(req)
      return auth ? `user:${auth.user.id}` : getDefaultKey(req)
    }
  })
}

// API key specific rate limiter
export function createApiKeyRateLimiter() {
  return createRateLimiter({
    windowMs: 60 * 60 * 1000, // 1 hour
    maxRequests: 1000, // Default limit
    keyGenerator: async (req) => {
      const authHeader = req.headers.get('authorization')
      if (authHeader?.startsWith('Bearer ')) {
        const token = authHeader.substring(7)

        // Check if it's an API key
        if (token.startsWith('sk_')) {
          const apiKeyHash = hashApiKey(token)
          const apiKey = await db.apiKey.findUnique({
            where: { keyHash: apiKeyHash },
            select: { id: true, rateLimit: true }
          })

          if (apiKey) {
            return `apikey:${apiKey.id}`
          }
        }
      }

      return getDefaultKey(req)
    },
    onLimitReached: async (req) => {
      // Log API key abuse
      console.warn('API key rate limit exceeded:', {
        ip: req.ip,
        userAgent: req.headers.get('user-agent'),
        timestamp: new Date().toISOString()
      })
    }
  })
}

