// middleware.ts - Complete middleware with rate limiting
import { NextResponse } from 'next/server'
import type { NextRequest } from 'next/server'
import { createRateLimiter, createUserRateLimiter, createApiKeyRateLimiter } from '@/lib/rate-limit'

// Different rate limiters for different endpoints
const globalRateLimiter = createRateLimiter({
  windowMs: 15 * 60 * 1000, // 15 minutes
  maxRequests: 100, // 100 requests per 15 minutes per IP
})

const authRateLimiter = createRateLimiter({
  windowMs: 60 * 60 * 1000, // 1 hour
  maxRequests: 5, // 5 login attempts per hour per IP
})

const userRateLimiter = createUserRateLimiter({
  windowMs: 60 * 60 * 1000, // 1 hour
  maxRequests: 1000, // 1000 requests per hour per user
})

const apiKeyRateLimiter = createApiKeyRateLimiter()

export async function middleware(request: NextRequest) {
  const { pathname } = request.nextUrl

  // Apply rate limiting based on path
  let rateLimitResponse: NextResponse | null = null

  if (pathname.startsWith('/api/auth')) {
    // Strict rate limiting for authentication endpoints
    rateLimitResponse = await authRateLimiter(request)
  } else if (pathname.startsWith('/api/ai/')) {
    // API key rate limiting for AI functions
    rateLimitResponse = await apiKeyRateLimiter(request)
  } else if (pathname.startsWith('/api/user/') || pathname.startsWith('/api/usage/')) {
    // User-specific rate limiting for dashboard APIs
    rateLimitResponse = await userRateLimiter(request)
  } else if (pathname.startsWith('/api/')) {
    // General API rate limiting
    rateLimitResponse = await globalRateLimiter(request)
  }

  // If rate limit exceeded, return the rate limit response
  if (rateLimitResponse) {
    return rateLimitResponse
  }

  // Continue with CORS headers for API routes
  if (pathname.startsWith('/api/')) {
    const response = NextResponse.next()

    // Add CORS headers
    response.headers.set('Access-Control-Allow-Origin', process.env.ALLOWED_ORIGINS || '*')
    response.headers.set('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS')
    response.headers.set('Access-Control-Allow-Headers', 'Content-Type, Authorization')

    // Security headers
    response.headers.set('X-Content-Type-Options', 'nosniff')
    response.headers.set('X-Frame-Options', 'DENY')
    response.headers.set('X-XSS-Protection', '1; mode=block')

    return response
  }

  return NextResponse.next()
}

export const config = {
  matcher: [
    '/api/:path*',
    '/((?!_next/static|_next/image|favicon.ico).*)',
  ]
}

