// ==================================================
// DATABASE TRANSACTIONS IMPLEMENTATION
// ==================================================

// lib/transactions.ts - Atomic database operations
import { db } from '@/lib/db'
import { Prisma } from '@prisma/client'

export class TransactionError extends Error {
  constructor(message: string, public readonly code: string) {
    super(message)
    this.name = 'TransactionError'
  }
}

// Credit operations with atomic transactions
export async function deductCreditsAtomic(
  userId: string,
  amount: number,
  usageEventId: string,
  metadata?: Record<string, any>
): Promise<{ success: boolean; newBalance: number }> {
  return await db.$transaction(async (tx) => {
    // Get current balance
    const currentBalance = await tx.creditLedger.aggregate({
      where: { userId },
      _sum: { delta: true }
    })

    const balance = currentBalance._sum.delta || 0

    if (balance < amount) {
      throw new TransactionError(
        `Insufficient credits. Required: ${amount}, Available: ${balance}`,
        'INSUFFICIENT_CREDITS'
      )
    }

    // Create credit deduction record
    await tx.creditLedger.create({
      data: {
        userId,
        delta: -amount,
        reason: 'usage',
        balance: balance - amount,
        usageEventId,
        metadata: metadata ? JSON.stringify(metadata) : null
      }
    })

    // Update usage event status
    await tx.usageEvent.update({
      where: { id: usageEventId },
      data: { 
        status: 'processing',
        creditsUsed: amount
      }
    })

    return {
      success: true,
      newBalance: balance - amount
    }
  }, {
    maxWait: 5000, // Wait up to 5 seconds for a transaction slot
    timeout: 10000, // Transaction timeout after 10 seconds
    isolationLevel: Prisma.TransactionIsolationLevel.Serializable
  })
}

// Purchase transaction with webhook safety
export async function processPurchaseTransaction(
  sessionId: string,
  packId: string,
  customerEmail: string,
  amountTotal: number,
  credits: number
): Promise<{ orderId: string; userId: string }> {
  return await db.$transaction(async (tx) => {
    // Check if order already processed (idempotency)
    const existingOrder = await tx.order.findUnique({
      where: { stripeSessionId: sessionId }
    })

    if (existingOrder && existingOrder.status === 'completed') {
      throw new TransactionError(
        'Order already processed',
        'ORDER_ALREADY_PROCESSED'
      )
    }

    // Get or create user
    let user = await tx.user.findUnique({
      where: { email: customerEmail }
    })

    if (!user) {
      user = await tx.user.create({
        data: {
          email: customerEmail,
          plan: 'starter',
          isActive: true
        }
      })
    }

    // Create or update order
    const order = await tx.order.upsert({
      where: { stripeSessionId: sessionId },
      update: {
        status: 'completed',
        processedAt: new Date()
      },
      create: {
        stripeSessionId: sessionId,
        packId,
        email: customerEmail,
        amount: amountTotal,
        status: 'completed',
        processedAt: new Date()
      }
    })

    // Get current credit balance
    const currentBalance = await tx.creditLedger.aggregate({
      where: { userId: user.id },
      _sum: { delta: true }
    })

    const balance = currentBalance._sum.delta || 0

    // Add credits
    await tx.creditLedger.create({
      data: {
        userId: user.id,
        delta: credits,
        reason: 'purchase',
        balance: balance + credits,
        orderId: order.id,
        metadata: JSON.stringify({
          packId,
          stripeSessionId: sessionId,
          amountPaid: amountTotal,
          purchaseDate: new Date().toISOString()
        })
      }
    })

    return {
      orderId: order.id,
      userId: user.id
    }
  })
}

// Refund transaction
export async function processRefundTransaction(
  orderId: string,
  refundAmount: number,
  reason: string
): Promise<void> {
  await db.$transaction(async (tx) => {
    const order = await tx.order.findUnique({
      where: { id: orderId },
      include: { creditLedger: true }
    })

    if (!order) {
      throw new TransactionError('Order not found', 'ORDER_NOT_FOUND')
    }

    if (order.status !== 'completed') {
      throw new TransactionError('Order not eligible for refund', 'INVALID_ORDER_STATUS')
    }

    // Find the credit addition from this purchase
    const creditAddition = order.creditLedger.find(
      entry => entry.reason === 'purchase' && entry.delta > 0
    )

    if (!creditAddition) {
      throw new TransactionError('Original credit transaction not found', 'CREDIT_TRANSACTION_NOT_FOUND')
    }

    // Check if user has enough credits to deduct
    const currentBalance = await tx.creditLedger.aggregate({
      where: { userId: order.user?.id },
      _sum: { delta: true }
    })

    const balance = currentBalance._sum.delta || 0
    const creditsToDeduct = creditAddition.delta

    if (balance < creditsToDeduct) {
      throw new TransactionError(
        `Cannot refund: user has used ${creditsToDeduct - balance} credits`,
        'INSUFFICIENT_CREDITS_FOR_REFUND'
      )
    }

    // Update order with refund info
    await tx.order.update({
      where: { id: orderId },
      data: {
        refundedAt: new Date(),
        refundAmount,
        status: 'refunded'
      }
    })

    // Deduct credits
    await tx.creditLedger.create({
      data: {
        userId: order.user!.id,
        delta: -creditsToDeduct,
        reason: 'refund',
        balance: balance - creditsToDeduct,
        orderId,
        metadata: JSON.stringify({
          refundAmount,
          reason,
          refundDate: new Date().toISOString()
        })
      }
    })
  })
}

