// app/api/admin/stats/route.ts
import { NextRequest, NextResponse } from 'next/server'
import { db } from '@/lib/db'

export async function GET(request: NextRequest) {
  try {
    // Get admin statistics
    const [userStats, revenueStats, creditStats, usageStats] = await Promise.all([
      // User statistics
      db.user.aggregate({
        _count: { id: true },
        where: { isActive: true }
      }),

      // Revenue statistics
      db.order.aggregate({
        _sum: { amount: true },
        _count: { id: true },
        where: { status: 'completed' }
      }),

      // Credit statistics
      db.creditLedger.aggregate({
        _sum: { delta: true },
        where: { reason: 'purchase' }
      }),

      // Usage statistics for today
      db.usageEvent.aggregate({
        _count: { id: true },
        _sum: { creditsUsed: true },
        _avg: { processingMs: true },
        where: {
          createdAt: {
            gte: new Date(new Date().setHours(0, 0, 0, 0))
          }
        }
      })
    ])

    // Calculate error rate
    const errorCount = await db.usageEvent.count({
      where: {
        status: 'error',
        createdAt: {
          gte: new Date(Date.now() - 24 * 60 * 60 * 1000) // Last 24 hours
        }
      }
    })

    const totalRequests = await db.usageEvent.count({
      where: {
        createdAt: {
          gte: new Date(Date.now() - 24 * 60 * 60 * 1000)
        }
      }
    })

    const errorRate = totalRequests > 0 ? (errorCount / totalRequests) * 100 : 0

    // Monthly revenue
    const monthlyRevenue = await db.order.aggregate({
      _sum: { amount: true },
      where: {
        status: 'completed',
        createdAt: {
          gte: new Date(new Date().getFullYear(), new Date().getMonth(), 1)
        }
      }
    })

    return NextResponse.json({
      totalUsers: userStats._count.id,
      activeUsers: userStats._count.id, // Simplified
      totalRevenue: (revenueStats._sum.amount || 0) / 100, // Convert from cents
      monthlyRevenue: (monthlyRevenue._sum.amount || 0) / 100,
      totalCreditsIssued: creditStats._sum.delta || 0,
      creditsUsedToday: usageStats._sum.creditsUsed || 0,
      errorRate: Math.round(errorRate * 100) / 100,
      avgResponseTime: Math.round(usageStats._avg.processingMs || 0)
    })

  } catch (error) {
    console.error('Admin stats error:', error)
    return NextResponse.json(
      { error: 'Failed to fetch admin statistics' },
      { status: 500 }
    )
  }
}

