// app/api/health/route.ts - Comprehensive health check
import { NextRequest, NextResponse } from 'next/server'
import { db } from '@/lib/db'

interface HealthCheck {
  status: 'healthy' | 'degraded' | 'unhealthy'
  timestamp: string
  checks: {
    database: { status: string; responseTime?: number; error?: string }
    redis?: { status: string; responseTime?: number; error?: string }
    external_apis: { status: string; checks: any[] }
    disk_space: { status: string; usage?: number }
    memory: { status: string; usage: number }
  }
}

export async function GET(request: NextRequest) {
  const startTime = Date.now()
  const health: HealthCheck = {
    status: 'healthy',
    timestamp: new Date().toISOString(),
    checks: {
      database: { status: 'unknown' },
      external_apis: { status: 'healthy', checks: [] },
      disk_space: { status: 'healthy' },
      memory: { status: 'healthy', usage: process.memoryUsage().heapUsed / 1024 / 1024 }
    }
  }

  // Database health check
  try {
    const dbStart = Date.now()
    await db.$queryRaw`SELECT 1`
    const dbTime = Date.now() - dbStart

    health.checks.database = {
      status: dbTime < 1000 ? 'healthy' : 'degraded',
      responseTime: dbTime
    }
  } catch (error) {
    health.checks.database = {
      status: 'unhealthy',
      error: error instanceof Error ? error.message : 'Database connection failed'
    }
    health.status = 'unhealthy'
  }

  // Memory check (warn if > 500MB)
  const memoryUsage = process.memoryUsage().heapUsed / 1024 / 1024
  health.checks.memory = {
    status: memoryUsage > 500 ? 'degraded' : 'healthy',
    usage: Math.round(memoryUsage)
  }

  // Overall status determination
  if (health.checks.database.status === 'unhealthy') {
    health.status = 'unhealthy'
  } else if (
    health.checks.database.status === 'degraded' ||
    health.checks.memory.status === 'degraded'
  ) {
    health.status = 'degraded'
  }

  const statusCode = health.status === 'unhealthy' ? 503 : 
                     health.status === 'degraded' ? 200 : 200

  return NextResponse.json(health, { status: statusCode })
}

// app/api/metrics/route.ts - Application metrics
export async function GET(request: NextRequest) {
  try {
    const now = new Date()
    const last24h = new Date(now.getTime() - 24 * 60 * 60 * 1000)
    const last7d = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000)

    const [usage24h, usage7d, errors24h, topUsers] = await Promise.all([
      // Usage metrics - last 24 hours
      db.usageEvent.groupBy({
        by: ['functionName', 'status'],
        where: { createdAt: { gte: last24h } },
        _count: { id: true },
        _avg: { processingMs: true }
      }),

      // Usage metrics - last 7 days
      db.usageEvent.groupBy({
        by: ['functionName'],
        where: { createdAt: { gte: last7d } },
        _count: { id: true },
        _sum: { creditsUsed: true }
      }),

      // Error metrics - last 24 hours
      db.usageEvent.groupBy({
        by: ['status', 'errorCode'],
        where: { 
          createdAt: { gte: last24h },
          status: 'error'
        },
        _count: { id: true }
      }),

      // Top users by usage
      db.usageEvent.groupBy({
        by: ['userId'],
        where: { createdAt: { gte: last7d } },
        _count: { id: true },
        _sum: { creditsUsed: true },
        orderBy: { _sum: { creditsUsed: 'desc' } },
        take: 10
      })
    ])

    return NextResponse.json({
      timestamp: now.toISOString(),
      usage_24h: usage24h,
      usage_7d: usage7d,
      errors_24h: errors24h,
      top_users: topUsers,
      system: {
        uptime: process.uptime(),
        memory: process.memoryUsage(),
        version: process.version
      }
    })

  } catch (error) {
    console.error('Metrics error:', error)
    return NextResponse.json(
      { error: 'Failed to fetch metrics' },
      { status: 500 }
    )
  }
}

// app/api/alerts/route.ts - Alert system
export async function GET(request: NextRequest) {
  try {
    const now = new Date()
    const alerts = []

    // Check for high error rate
    const recentErrors = await db.usageEvent.count({
      where: {
        status: 'error',
        createdAt: { gte: new Date(now.getTime() - 60 * 60 * 1000) } // Last hour
      }
    })

    const totalRequests = await db.usageEvent.count({
      where: {
        createdAt: { gte: new Date(now.getTime() - 60 * 60 * 1000) }
      }
    })

    if (totalRequests > 0) {
      const errorRate = (recentErrors / totalRequests) * 100
      if (errorRate > 5) { // More than 5% error rate
        alerts.push({
          id: 'high_error_rate',
          level: 'critical',
          message: `High error rate detected: ${errorRate.toFixed(2)}%`,
          timestamp: now.toISOString()
        })
      }
    }

    // Check for slow response times
    const avgResponseTime = await db.usageEvent.aggregate({
      _avg: { processingMs: true },
      where: {
        createdAt: { gte: new Date(now.getTime() - 60 * 60 * 1000) },
        status: 'success'
      }
    })

    if (avgResponseTime._avg.processingMs && avgResponseTime._avg.processingMs > 5000) {
      alerts.push({
        id: 'slow_response',
        level: 'warning',
        message: `Slow response times: ${Math.round(avgResponseTime._avg.processingMs)}ms average`,
        timestamp: now.toISOString()
      })
    }

    // Check for users with insufficient credits making requests
    const insufficientCreditAttempts = await db.usageEvent.count({
      where: {
        status: 'error',
        errorMessage: { contains: 'Insufficient credits' },
        createdAt: { gte: new Date(now.getTime() - 24 * 60 * 60 * 1000) }
      }
    })

    if (insufficientCreditAttempts > 10) {
      alerts.push({
        id: 'insufficient_credits',
        level: 'info',
        message: `${insufficientCreditAttempts} requests failed due to insufficient credits today`,
        timestamp: now.toISOString()
      })
    }

    return NextResponse.json({
      alerts,
      total: alerts.length,
      timestamp: now.toISOString()
    })

  } catch (error) {
    console.error('Alerts error:', error)
    return NextResponse.json(
      { error: 'Failed to fetch alerts' },
      { status: 500 }
    )
  }
}


