// lib/ai-functions.ts - Complete GPT function processing
import crypto from 'crypto'
import { db } from '@/lib/db'

export interface AIResult {
  text: string
  inputTokens?: number
  outputTokens?: number
  model?: string
  fromCache?: boolean
  cacheKey?: string
}

export interface AIOptions {
  model?: string
  maxTokens?: number
  temperature?: number
  language?: string
  format?: string
  style?: string
  columns?: string[]
  rows?: number
  classification_categories?: string[]
  extraction_fields?: string[]
  [key: string]: any
}

// Cost mapping for different functions
const FUNCTION_COSTS: Record<string, number> = {
  'gpt': 1,
  'gpt_list': 2,
  'gpt_hlist': 2,
  'gpt_table': 3,
  'gpt_summarize': 1,
  'gpt_edit': 2,
  'gpt_extract': 2,
  'gpt_classify': 1,
  'gpt_translate': 2,
  'gpt_format': 1,
  'gpt_split': 1,
  'gpt_hsplit': 1,
  'gpt_vision': 5,
  'gpt_create_prompt': 1,
  'gpt_match': 3 // Higher cost due to embeddings
}

// Cache management
interface CacheEntry {
  id: string
  key: string
  result: AIResult
  expiresAt: Date
  createdAt: Date
  functionName: string
}

// Generate cache key from function name, input, and options
export function generateCacheKey(
  functionName: string,
  input: string,
  options: AIOptions = {}
): string {
  const normalizedOptions = {
    model: options.model || 'default',
    temperature: options.temperature || 0.7,
    maxTokens: options.maxTokens || 1000,
    ...Object.fromEntries(
      Object.entries(options)
        .filter(([key]) => !['apiKey', 'userId'].includes(key))
        .sort(([a], [b]) => a.localeCompare(b))
    )
  }

  const payload = JSON.stringify({
    function: functionName,
    input: input.trim(),
    options: normalizedOptions
  })

  return crypto.createHash('sha256').update(payload).digest('hex')
}

// Get cached result
export async function getCachedResult(cacheKey: string): Promise<AIResult | null> {
  try {
    const cached = await db.functionCache.findUnique({
      where: { 
        key: cacheKey,
        expiresAt: { gt: new Date() }
      }
    })

    if (cached) {
      const result: AIResult = JSON.parse(cached.result)
      result.fromCache = true
      result.cacheKey = cacheKey

      // Update last accessed time
      await db.functionCache.update({
        where: { id: cached.id },
        data: { lastAccessed: new Date() }
      })

      return result
    }
  } catch (error) {
    console.error('Cache retrieval error:', error)
  }

  return null
}

// Store result in cache
export async function setCachedResult(
  cacheKey: string,
  functionName: string,
  result: AIResult
): Promise<void> {
  try {
    const expiresAt = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000) // 30 days

    await db.functionCache.upsert({
      where: { key: cacheKey },
      update: {
        result: JSON.stringify(result),
        expiresAt,
        lastAccessed: new Date()
      },
      create: {
        key: cacheKey,
        functionName,
        result: JSON.stringify(result),
        expiresAt,
        lastAccessed: new Date()
      }
    })
  } catch (error) {
    console.error('Cache storage error:', error)
  }
}

// Main AI function processor with caching
export async function processAIFunction(
  functionName: string,
  inputText: string,
  options: AIOptions = {},
  useCache: boolean = true
): Promise<AIResult> {
  const normalizedFunctionName = functionName.toLowerCase()

  // Generate cache key
  const cacheKey = generateCacheKey(normalizedFunctionName, inputText, options)

  // Try to get cached result first
  if (useCache) {
    const cachedResult = await getCachedResult(cacheKey)
    if (cachedResult) {
      return cachedResult
    }
  }

  // Process the function
  let result: AIResult

  try {
    switch (normalizedFunctionName) {
      case 'gpt':
        result = await processGPT(inputText, options)
        break
      case 'gpt_list':
        result = await processGPTList(inputText, options, false)
        break
      case 'gpt_hlist':
        result = await processGPTList(inputText, options, true)
        break
      case 'gpt_table':
        result = await processGPTTable(inputText, options)
        break
      case 'gpt_summarize':
        result = await processGPTSummarize(inputText, options)
        break
      case 'gpt_edit':
        result = await processGPTEdit(inputText, options)
        break
      case 'gpt_extract':
        result = await processGPTExtract(inputText, options)
        break
      case 'gpt_classify':
        result = await processGPTClassify(inputText, options)
        break
      case 'gpt_translate':
        result = await processGPTTranslate(inputText, options)
        break
      case 'gpt_format':
        result = await processGPTFormat(inputText, options)
        break
      case 'gpt_split':
        result = await processGPTSplit(inputText, options, false)
        break
      case 'gpt_hsplit':
        result = await processGPTSplit(inputText, options, true)
        break
      case 'gpt_vision':
        result = await processGPTVision(inputText, options)
        break
      case 'gpt_create_prompt':
        result = await processGPTCreatePrompt(inputText, options)
        break
      case 'gpt_match':
        result = await processGPTMatch(inputText, options)
        break
      default:
        throw new Error(`Unknown function: ${functionName}`)
    }

    // Cache the result
    if (useCache && result) {
      await setCachedResult(cacheKey, normalizedFunctionName, result)
    }

    return result

  } catch (error) {
    console.error(`AI function ${functionName} failed:`, error)
    throw error
  }
}

