// GPT_LIST and GPT_HLIST - Generate lists (vertical or horizontal)
async function processGPTList(input: string, options: AIOptions, horizontal: boolean): Promise<AIResult> {
  const count = options.count || 5
  const style = options.style || 'numbered'

  const prompt = `Generate a ${count}-item ${style} list based on: ${input}. ${
    horizontal ? 'Format as a single row separated by commas.' : 'Format as a vertical list.'
  }`

  // Demo implementation
  const items = Array.from({ length: count }, (_, i) => `Item ${i + 1} for ${input}`)
  const response = horizontal 
    ? items.join(', ')
    : items.map((item, i) => `${i + 1}. ${item}`).join('\n')

  return {
    text: response,
    inputTokens: estimateTokens(input + prompt),
    outputTokens: estimateTokens(response),
    model: options.model || 'gpt-3.5-turbo'
  }
}

// GPT_TABLE - Generate structured tables
async function processGPTTable(input: string, options: AIOptions): Promise<AIResult> {
  const columns = options.columns || ['Column 1', 'Column 2', 'Column 3']
  const rows = options.rows || 3

  const prompt = `Generate a ${rows}-row table with columns [${columns.join(', ')}] based on: ${input}`

