// GPT_FORMAT - Text formatting
async function processGPTFormat(input: string, options: AIOptions): Promise<AIResult> {
  const format = options.format || 'clean'

  let formatted: string

  switch (format) {
    case 'uppercase':
      formatted = input.toUpperCase()
      break
    case 'lowercase':
      formatted = input.toLowerCase()
      break
    case 'title':
      formatted = input.replace(/\w\S*/g, txt => 
        txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
      )
      break
    case 'clean':
    default:
      formatted = input
        .replace(/\s+/g, ' ')
        .replace(/[^\w\s.,!?-]/g, '')
        .trim()
      break
  }

  return {
    text: formatted,
    inputTokens: estimateTokens(input),
    outputTokens: estimateTokens(formatted),
    model: 'formatting'
  }
}

