export async function getCacheStats(): Promise<{
  totalEntries: number
  totalSize: number
  expiredEntries: number
  mostUsedFunctions: Array<{ function: string; count: number }>
}> {
  const [total, expired, functionStats] = await Promise.all([
    db.functionCache.count(),
    db.functionCache.count({
      where: { expiresAt: { lt: new Date() } }
    }),
    db.functionCache.groupBy({
      by: ['functionName'],
      _count: { id: true },
      orderBy: { _count: { id: 'desc' } },
      take: 10
    })
  ])

  return {
    totalEntries: total,
    totalSize: 0, // Could calculate from result field lengths
    expiredEntries: expired,
    mostUsedFunctions: functionStats.map(stat => ({
      function: stat.functionName,
      count: stat._count.id
    }))
  }
}

