// app/api/ai/[function]/route.ts - Updated to handle all functions
import { NextRequest, NextResponse } from 'next/server'
import { authenticateUser } from '@/lib/auth'
import { processAIFunction, FUNCTION_COSTS } from '@/lib/ai-functions'
import { deductCreditsAtomic } from '@/lib/transactions'
import { getCurrentCreditBalance } from '@/lib/db'

export async function POST(
  request: NextRequest,
  { params }: { params: { function: string } }
) {
  const startTime = Date.now()
  let usageEventId: string | null = null

  try {
    const user = await authenticateUser(request)
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const functionName = params.function.toLowerCase()
    const creditCost = FUNCTION_COSTS[functionName]

    if (!creditCost) {
      return NextResponse.json({ error: 'Invalid function' }, { status: 400 })
    }

    // Check credit balance
    const currentBalance = await getCurrentCreditBalance(user.user.id)
    if (currentBalance < creditCost) {
      return NextResponse.json({ 
        error: 'Insufficient credits',
        required: creditCost,
        available: currentBalance
      }, { status: 402 })
    }

    const body = await request.json()
    const { input, options = {}, useCache = true } = body

    // Validate input
    if (!input || typeof input !== 'string') {
      return NextResponse.json({ error: 'Invalid input' }, { status: 400 })
    }

    // Special validation for GPT_MATCH
    if (functionName === 'gpt_match' && options.dataset) {
      if (!Array.isArray(options.dataset)) {
        return NextResponse.json({ error: 'Dataset must be an array' }, { status: 400 })
      }
      if (options.dataset.length > 1000) {
        return NextResponse.json({ 
          error: 'GPT_MATCH is limited to 1000 rows of data',
          provided: options.dataset.length 
        }, { status: 400 })
      }
    }

    // Create usage event
    const usageEvent = await db.usageEvent.create({
      data: {
        userId: user.user.id,
        functionName: functionName.toUpperCase(),
        creditsUsed: creditCost,
        status: 'pending',
        ipAddress: request.ip || 'unknown',
        userAgent: request.headers.get('user-agent'),
        metadata: JSON.stringify({ input: input.substring(0, 100), options })
      }
    })
    usageEventId = usageEvent.id

    // Process AI function
    const result = await processAIFunction(functionName, input, options, useCache)

    // Only deduct credits if not from cache
    if (!result.fromCache) {
      await deductCreditsAtomic(user.user.id, creditCost, usageEventId, {
        functionName,
        fromCache: false,
        cacheKey: result.cacheKey
      })
    } else {
      // Update usage event for cache hit
      await db.usageEvent.update({
        where: { id: usageEventId },
        data: {
          status: 'success',
          creditsUsed: 0, // No credits charged for cache hits
          processingMs: Date.now() - startTime,
          metadata: JSON.stringify({
            ...JSON.parse(usageEvent.metadata || '{}'),
            fromCache: true,
            cacheKey: result.cacheKey
          })
        }
      })
    }

    // Update usage event with success
    if (!result.fromCache) {
      await db.usageEvent.update({
        where: { id: usageEventId },
        data: {
          status: 'success',
          processingMs: Date.now() - startTime,
          inputTokens: result.inputTokens,
          outputTokens: result.outputTokens
        }
      })
    }

    const newBalance = result.fromCache 
      ? currentBalance 
      : currentBalance - creditCost

    return NextResponse.json({
      result: result.text,
      creditsUsed: result.fromCache ? 0 : creditCost,
      remainingCredits: newBalance,
      processingTime: Date.now() - startTime,
      fromCache: result.fromCache || false,
      functionName: functionName.toUpperCase(),
      inputTokens: result.inputTokens,
      outputTokens: result.outputTokens,
      model: result.model
    })

  } catch (error) {
    // Update usage event with error
    if (usageEventId) {
      await db.usageEvent.update({
        where: { id: usageEventId },
        data: {
          status: 'error',
          errorMessage: error instanceof Error ? error.message : 'Unknown error',
          processingMs: Date.now() - startTime
        }
      }).catch(console.error)
    }

    console.error(`Function ${params.function} error:`, error)
    return NextResponse.json({ 
      error: 'Function execution failed',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 })
  }
}

