// lib/bulk-tools.ts - Core bulk processing system
import { db } from '@/lib/db'
import { processAIFunction } from '@/lib/ai-functions'
import { deductCreditsAtomic } from '@/lib/transactions'

export interface BulkToolConfig {
  name: string
  type: 'custom_prompt' | 'translate' | 'classify' | 'extract' | 'reformat' | 'web_search' | 'vision'
  options: Record<string, any>
  customInstructions?: string
  sources?: boolean // Whether to include sources column
}

export interface BulkProcessingJob {
  id: string
  userId: string
  toolConfig: BulkToolConfig
  inputData: Array<Record<string, any>>
  status: 'pending' | 'processing' | 'completed' | 'failed' | 'paused'
  progress: {
    total: number
    completed: number
    failed: number
    startedAt: Date
    estimatedCompletion?: Date
  }
  results: Array<{
    row: number
    result?: string
    sources?: string[]
    error?: string
    creditsUsed: number
  }>
  totalCreditsUsed: number
  createdAt: Date
  updatedAt: Date
}

// Rate limiting configurations
const RATE_LIMITS = {
  FORMULAS: {
    rpm: 120, // requests per minute
    burst: 20 // burst allowance
  },
  BULK: {
    rpm: 360,
    burst: 60,
    maxConcurrent: 10 // max concurrent bulk jobs
  }
}

// Create bulk processing job
export async function createBulkJob(
  userId: string,
  toolConfig: BulkToolConfig,
  inputData: Array<Record<string, any>>
): Promise<string> {

  // Validate input data
  if (!inputData.length || inputData.length > 10000) {
    throw new Error('Input data must contain 1-10,000 rows')
  }

  // Check for image columns if using vision
  if (toolConfig.type === 'vision') {
    const imageColumns = Object.keys(inputData[0]).filter(key => 
      key.toLowerCase().includes('image') || key.toLowerCase().includes('url')
    )

    if (imageColumns.length > 5) {
      throw new Error('Maximum 5 image columns supported')
    }
  }

  // Create job record
  const job = await db.bulkJob.create({
    data: {
      userId,
      toolConfig: JSON.stringify(toolConfig),
      inputData: JSON.stringify(inputData),
      status: 'pending',
      totalRows: inputData.length,
      completedRows: 0,
      failedRows: 0,
      totalCreditsUsed: 0,
      results: JSON.stringify([])
    }
  })

  // Start processing asynchronously
  processBulkJobAsync(job.id)

  return job.id
}

// Async bulk job processor
async function processBulkJobAsync(jobId: string): Promise<void> {
  try {
    const job = await db.bulkJob.findUnique({ where: { id: jobId } })
    if (!job) return

    const toolConfig: BulkToolConfig = JSON.parse(job.toolConfig)
    const inputData: Array<Record<string, any>> = JSON.parse(job.inputData)
    const results: any[] = JSON.parse(job.results || '[]')

    // Update status to processing
    await db.bulkJob.update({
      where: { id: jobId },
      data: { 
        status: 'processing',
        startedAt: new Date()
      }
    })

    // Process in batches to respect rate limits
    const batchSize = toolConfig.type === 'web_search' ? 5 : 10
    let totalCreditsUsed = 0

    for (let i = 0; i < inputData.length; i += batchSize) {
      const batch = inputData.slice(i, i + batchSize)

      // Process batch with rate limiting
      const batchPromises = batch.map(async (row, batchIndex) => {
        const rowIndex = i + batchIndex

        try {
          // Apply rate limiting delay
          await new Promise(resolve => setTimeout(resolve, 60000 / RATE_LIMITS.BULK.rpm))

          const result = await processBulkRow(job.userId, toolConfig, row, rowIndex)
          totalCreditsUsed += result.creditsUsed

          return {
            row: rowIndex,
            result: result.text,
            sources: result.sources,
            creditsUsed: result.creditsUsed
          }
        } catch (error) {
          return {
            row: rowIndex,
            error: error instanceof Error ? error.message : 'Processing failed',
            creditsUsed: 0
          }
        }
      })

      const batchResults = await Promise.all(batchPromises)
      results.push(...batchResults)

      // Update progress
      const completed = results.filter(r => !r.error).length
      const failed = results.filter(r => r.error).length

      await db.bulkJob.update({
        where: { id: jobId },
        data: {
          completedRows: completed,
          failedRows: failed,
          totalCreditsUsed: totalCreditsUsed,
          results: JSON.stringify(results),
          estimatedCompletion: new Date(Date.now() + 
            (inputData.length - results.length) * (60000 / RATE_LIMITS.BULK.rpm)
          )
        }
      })
    }

    // Mark job as completed
    await db.bulkJob.update({
      where: { id: jobId },
      data: {
        status: 'completed',
        completedAt: new Date()
      }
    })

  } catch (error) {
    console.error(`Bulk job ${jobId} failed:`, error)

    await db.bulkJob.update({
      where: { id: jobId },
      data: {
        status: 'failed',
        errorMessage: error instanceof Error ? error.message : 'Unknown error'
      }
    })
  }
}

// Process individual row based on tool type
async function processBulkRow(
  userId: string,
  toolConfig: BulkToolConfig,
  rowData: Record<string, any>,
  rowIndex: number
): Promise<{ text: string; sources?: string[]; creditsUsed: number }> {

  const { type, options, customInstructions, sources: includeSources } = toolConfig

  let input: string
  let processingOptions = { ...options }

  // Apply custom instructions if provided
  if (customInstructions) {
    processingOptions.instructions = customInstructions
  }

  switch (type) {
    case 'custom_prompt':
      input = interpolateTemplate(options.template, rowData)
      return await processCustomPrompt(userId, input, processingOptions)

    case 'translate':
      input = getColumnValue(rowData, options.inputColumn)
      processingOptions.language = options.targetLanguage
      return await processTranslate(userId, input, processingOptions)

    case 'classify':
      input = getColumnValue(rowData, options.inputColumn)
      processingOptions.classification_categories = options.categories
      return await processClassify(userId, input, processingOptions)

    case 'extract':
      input = getColumnValue(rowData, options.inputColumn)
      processingOptions.extraction_fields = options.fields
      return await processExtract(userId, input, processingOptions)

    case 'reformat':
      input = getColumnValue(rowData, options.inputColumn)
      processingOptions.format = options.format
      return await processReformat(userId, input, processingOptions)

    case 'web_search':
      input = getColumnValue(rowData, options.inputColumn)
      return await processWebSearch(userId, input, processingOptions, includeSources)

    case 'vision':
      return await processVision(userId, rowData, processingOptions)

    default:
      throw new Error(`Unknown tool type: ${type}`)
  }
}

