async function processWebSearch(
  userId: string,
  query: string,
  options: any,
  includeSources?: boolean
): Promise<{ text: string; sources?: string[]; creditsUsed: number }> {

  const searchOptions = {
    domains: options.domains || [], // up to 3 domains
    recency: options.recency || 'any', // last_day, last_week, last_month, any
    contextSize: options.contextSize || 'medium', // small, medium, large
    maxResults: options.maxResults || 5
  }

  // Simulate web search (integrate with Perplexity/Gemini in production)
  const searchResults = await performWebSearch(query, searchOptions)

  // Extract sources if requested
  const sources = includeSources ? searchResults.map(r => r.url) : undefined

  // Combine results into response
  const combinedText = searchResults
    .map(r => `${r.title}: ${r.snippet}`)
    .join('\n\n')

  return {
    text: combinedText,
    sources,
    creditsUsed: 3 // Higher cost for web search
  }
}

async function processVision(
  userId: string,
  rowData: Record<string, any>,
  options: any
): Promise<{ text: string; creditsUsed: number }> {

  // Find image columns (up to 5 supported)
  const imageColumns = Object.keys(rowData).filter(key => 
    key.toLowerCase().includes('image') || 
    key.toLowerCase().includes('url') ||
    key.toLowerCase().includes('photo')
  ).slice(0, 5)

  if (imageColumns.length === 0) {
    throw new Error('No image columns found')
  }

  // Process images
  const imageInputs = imageColumns
    .map(col => getColumnValue(rowData, col))
    .filter(Boolean)
    .join(', ')

  const result = await processAIFunction('gpt_vision', imageInputs, options, true)

  return {
    text: result.text,
    creditsUsed: result.fromCache ? 0 : 5 * imageColumns.length // 5 credits per image
  }
}

