import { NextRequest, NextResponse } from 'next/server'
import { authenticateUser } from '@/lib/auth'
import { createBulkJob } from '@/lib/bulk-tools'

export async function POST(request: NextRequest) {
  try {
    const user = await authenticateUser(request)
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const { toolConfig, inputData } = await request.json()

    const jobId = await createBulkJob(user.user.id, toolConfig, inputData)

    return NextResponse.json({ 
      jobId,
      message: 'Bulk job created successfully' 
    })

  } catch (error) {
    console.error('Bulk job creation error:', error)
    return NextResponse.json(
      { error: error instanceof Error ? error.message : 'Job creation failed' },
      { status: 500 }
    )
  }
}

// app/api/bulk/jobs/route.ts - Get user's job history
export async function GET(request: NextRequest) {
  try {
    const user = await authenticateUser(request)
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const jobs = await db.bulkJob.findMany({
      where: { 
        userId: user.user.id,
        createdAt: {
          gte: new Date(Date.now() - 90 * 24 * 60 * 60 * 1000) // 90 days
        }
      },
      orderBy: { createdAt: 'desc' },
      take: 50
    })

    return NextResponse.json(jobs.map(job => ({
      id: job.id,
      toolConfig: JSON.parse(job.toolConfig),
      status: job.status,
      progress: {
        total: job.totalRows,
        completed: job.completedRows,
        failed: job.failedRows,
        startedAt: job.startedAt,
        estimatedCompletion: job.estimatedCompletion
      },
      totalCreditsUsed: job.totalCreditsUsed,
      createdAt: job.createdAt
    })))

  } catch (error) {
    console.error('Job history error:', error)
    return NextResponse.json(
      { error: 'Failed to fetch job history' },
      { status: 500 }
    )
  }
}


