// lib/ai-providers/base-adapter.ts
export abstract class BaseProviderAdapter {
  constructor(protected config: ProviderConfig) {}

  abstract generate(request: GenerateRequest): Promise<GenerateResponse>
  abstract embed?(request: EmbedRequest): Promise<EmbedResponse>

  protected async makeRequest(
    endpoint: string,
    payload: any,
    options: { timeout?: number } = {}
  ): Promise<any> {
    const controller = new AbortController()
    const timeout = setTimeout(() => controller.abort(), options.timeout || 30000)

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.config.apiKey}`,
          'User-Agent': 'AI-Sheets/1.0',
          ...this.config.headers
        },
        body: JSON.stringify(payload),
        signal: controller.signal
      })

      if (!response.ok) {
        const error = await response.text()
        throw new Error(`Provider API error (${response.status}): ${error}`)
      }

      return await response.json()
    } finally {
      clearTimeout(timeout)
    }
  }

  protected normalizeMessages(messages: GenerateRequest['messages']): any[] {
    return messages.map(msg => ({
      role: msg.role,
      content: msg.content
    }))
  }
}

