// lib/ai-providers/openai-adapter.ts
export class OpenAIAdapter extends BaseProviderAdapter {
  constructor(config: ProviderConfig) {
    super({
      ...config,
      baseUrl: config.baseUrl || 'https://api.openai.com/v1'
    })
  }

  async generate(request: GenerateRequest): Promise<GenerateResponse> {
    const payload = {
      model: request.model,
      messages: this.normalizeMessages(request.messages),
      max_tokens: request.maxTokens,
      temperature: request.temperature,
      stream: false,
      ...(request.responseFormat === 'json' && {
        response_format: { type: 'json_object' }
      })
    }

    const response = await this.makeRequest(
      `${this.config.baseUrl}/chat/completions`,
      payload
    )

    return {
      content: response.choices[0]?.message?.content || '',
      usage: {
        promptTokens: response.usage?.prompt_tokens || 0,
        completionTokens: response.usage?.completion_tokens || 0,
        totalTokens: response.usage?.total_tokens || 0
      },
      model: response.model,
      finishReason: this.normalizeFinishReason(response.choices[0]?.finish_reason),
      provider: this.config.name
    }
  }

  async embed(request: EmbedRequest): Promise<EmbedResponse> {
    const payload = {
      model: request.model,
      input: request.input,
      dimensions: request.dimensions
    }

    const response = await this.makeRequest(
      `${this.config.baseUrl}/embeddings`,
      payload
    )

    return {
      embeddings: response.data.map((item: any) => item.embedding),
      usage: {
        totalTokens: response.usage?.total_tokens || 0
      },
      model: response.model,
      provider: this.config.name
    }
  }

  private normalizeFinishReason(reason: string): GenerateResponse['finishReason'] {
    switch (reason) {
      case 'stop': return 'stop'
      case 'length': return 'length'
      case 'tool_calls': return 'tool_calls'
      case 'content_filter': return 'content_filter'
      default: return 'stop'
    }
  }
}

