// lib/ai-providers/gemini-adapter.ts
export class GeminiAdapter extends BaseProviderAdapter {
  constructor(config: ProviderConfig) {
    super({
      ...config,
      baseUrl: config.baseUrl || 'https://generativelanguage.googleapis.com/v1beta'
    })
  }

  async generate(request: GenerateRequest): Promise<GenerateResponse> {
    const contents = this.convertMessagesForGemini(request.messages)

    const payload = {
      contents,
      generationConfig: {
        maxOutputTokens: request.maxTokens,
        temperature: request.temperature,
        ...(request.responseFormat === 'json' && {
          responseMimeType: 'application/json'
        })
      }
    }

    const response = await this.makeRequest(
      `${this.config.baseUrl}/models/${request.model}:generateContent?key=${this.config.apiKey}`,
      payload
    )

    const candidate = response.candidates?.[0]

    return {
      content: candidate?.content?.parts?.[0]?.text || '',
      usage: {
        promptTokens: response.usageMetadata?.promptTokenCount || 0,
        completionTokens: response.usageMetadata?.candidatesTokenCount || 0,
        totalTokens: response.usageMetadata?.totalTokenCount || 0
      },
      model: request.model,
      finishReason: this.normalizeGeminiFinishReason(candidate?.finishReason),
      provider: this.config.name
    }
  }

  private convertMessagesForGemini(messages: GenerateRequest['messages']) {
    return messages.map(msg => ({
      role: msg.role === 'assistant' ? 'model' : 'user',
      parts: [{ text: msg.content }]
    }))
  }

  private normalizeGeminiFinishReason(reason: string): GenerateResponse['finishReason'] {
    switch (reason) {
      case 'STOP': return 'stop'
      case 'MAX_TOKENS': return 'length'
      case 'SAFETY': return 'content_filter'
      default: return 'stop'
    }
  }
}

