// lib/ai-providers/factory.ts
export class ProviderFactory {
  private static adapters = new Map<string, typeof BaseProviderAdapter>([
    ['openai', OpenAIAdapter],
    ['anthropic', AnthropicAdapter],
    ['gemini', GeminiAdapter],
    ['perplexity', OpenAIAdapter], // Uses OpenAI-compatible API
    ['openrouter', OpenAIAdapter],
    ['deepseek', OpenAIAdapter],
    ['xai', OpenAIAdapter],
    ['mistral', OpenAIAdapter],
    ['custom', OpenAIAdapter] // Assumes OpenAI-compatible
  ])

  static create(config: ProviderConfig): BaseProviderAdapter {
    const AdapterClass = this.adapters.get(config.type)
    if (!AdapterClass) {
      throw new Error(`Unsupported provider type: ${config.type}`)
    }

    return new AdapterClass(config)
  }

  static getSupportedProviders(): string[] {
    return Array.from(this.adapters.keys())
  }
}

