// lib/ai-providers/manager.ts
export class ProviderManager {
  private systemProviders = new Map<string, BaseProviderAdapter>()
  private userProviders = new Map<string, BaseProviderAdapter>()

  constructor() {
    this.initializeSystemProviders()
  }

  private initializeSystemProviders() {
    const systemConfigs: ProviderConfig[] = [
      {
        name: 'openai-system',
        type: 'openai',
        apiKey: process.env.OPENAI_API_KEY || '',
        models: {
          generate: ['gpt-3.5-turbo', 'gpt-4', 'gpt-4-turbo'],
          embed: ['text-embedding-ada-002', 'text-embedding-3-small']
        },
        rateLimits: { rpm: 3000, tpm: 250000 }
      },
      {
        name: 'anthropic-system',
        type: 'anthropic',
        apiKey: process.env.ANTHROPIC_API_KEY || '',
        models: {
          generate: ['claude-3-haiku', 'claude-3-sonnet', 'claude-3-opus']
        },
        rateLimits: { rpm: 1000, tpm: 100000 }
      },
      {
        name: 'gemini-system',
        type: 'gemini',
        apiKey: process.env.GEMINI_API_KEY || '',
        models: {
          generate: ['gemini-pro', 'gemini-pro-vision']
        },
        rateLimits: { rpm: 60, tpm: 32000 }
      }
    ]

    systemConfigs.forEach(config => {
      if (config.apiKey) {
        const adapter = ProviderFactory.create(config)
        this.systemProviders.set(config.name, adapter)
      }
    })
  }

  async loadUserProvider(userId: string, providerId: string): Promise<BaseProviderAdapter | null> {
    const cacheKey = `${userId}:${providerId}`

    if (this.userProviders.has(cacheKey)) {
      return this.userProviders.get(cacheKey)!
    }

    const userKey = await db.userApiKey.findFirst({
      where: {
        userId,
        provider: providerId,
        isActive: true
      }
    })

    if (!userKey) {
      return null
    }

    try {
      const decryptedKey = KMSEncryption.decryptApiKey(userKey.encryptedKey)

      const config: ProviderConfig = {
        name: `${providerId}-user`,
        type: this.getProviderType(providerId),
        apiKey: decryptedKey,
        models: {
          generate: userKey.models
        },
        rateLimits: this.getUserRateLimits(providerId)
      }

      const adapter = ProviderFactory.create(config)
      this.userProviders.set(cacheKey, adapter)

      // Update last used
      await db.userApiKey.update({
        where: { id: userKey.id },
        data: { lastUsed: new Date() }
      })

      return adapter

    } catch (error) {
      console.error(`Failed to load user provider ${providerId}:`, error)
      return null
    }
  }

  getAvailableModels(userId?: string): Record<string, { requiresApiKey: boolean; models: string[] }> {
    const result: Record<string, { requiresApiKey: boolean; models: string[] }> = {}

    // System models (don't require user API key)
    this.systemProviders.forEach((adapter, name) => {
      const providerName = name.replace('-system', '')
      result[providerName] = {
        requiresApiKey: false,
        models: this.getSystemModels(providerName)
      }
    })

    // Models that require user API keys
    const allProviders = ['openai', 'anthropic', 'gemini', 'perplexity', 'openrouter', 'deepseek', 'xai', 'mistral']
    allProviders.forEach(provider => {
      if (!result[provider]) {
        result[provider] = {
          requiresApiKey: true,
          models: this.getAllModelsForProvider(provider)
        }
      }
    })

    return result
  }

  async generate(
    request: GenerateRequest,
    userId?: string
  ): Promise<GenerateResponse> {
    // Try user provider first if userId provided
    if (userId) {
      const provider = this.extractProviderFromModel(request.model)
      const userAdapter = await this.loadUserProvider(userId, provider)

      if (userAdapter) {
        return await userAdapter.generate(request)
      }
    }

    // Fall back to system provider
    const provider = this.extractProviderFromModel(request.model)
    const systemAdapter = this.systemProviders.get(`${provider}-system`)

    if (!systemAdapter) {
      throw new Error(`No provider available for model: ${request.model}`)
    }

    return await systemAdapter.generate(request)
  }

  async embed(
    request: EmbedRequest,
    userId?: string
  ): Promise<EmbedResponse> {
    // Similar logic to generate()
    if (userId) {
      const provider = this.extractProviderFromModel(request.model)
      const userAdapter = await this.loadUserProvider(userId, provider)

      if (userAdapter && userAdapter.embed) {
        return await userAdapter.embed(request)
      }
    }

    const provider = this.extractProviderFromModel(request.model)
    const systemAdapter = this.systemProviders.get(`${provider}-system`)

    if (!systemAdapter || !systemAdapter.embed) {
      throw new Error(`No embedding provider available for model: ${request.model}`)
    }

    return await systemAdapter.embed(request)
  }

  private extractProviderFromModel(model: string): string {
    if (model.startsWith('gpt-')) return 'openai'
    if (model.startsWith('claude-')) return 'anthropic'
    if (model.startsWith('gemini-')) return 'gemini'
    if (model.includes('perplexity')) return 'perplexity'
    // Add more model -> provider mappings
    return 'openai' // default
  }

  private getProviderType(providerId: string): ProviderConfig['type'] {
    const typeMap: Record<string, ProviderConfig['type']> = {
      'openai': 'openai',
      'anthropic': 'anthropic',
      'gemini': 'gemini',
      'perplexity': 'perplexity',
      'openrouter': 'openrouter',
      'deepseek': 'deepseek',
      'xai': 'xai',
      'mistral': 'mistral'
    }

    return typeMap[providerId] || 'custom'
  }

  private getUserRateLimits(providerId: string): ProviderConfig['rateLimits'] {
    // Conservative rate limits for user API keys
    const limits: Record<string, ProviderConfig['rateLimits']> = {
      'openai': { rpm: 500, tpm: 50000 },
      'anthropic': { rpm: 100, tpm: 20000 },
      'gemini': { rpm: 30, tpm: 16000 }
    }

    return limits[providerId] || { rpm: 100, tpm: 10000 }
  }

  private getSystemModels(provider: string): string[] {
    const systemModels: Record<string, string[]> = {
      'openai': ['gpt-3.5-turbo', 'gpt-4'],
      'anthropic': ['claude-3-haiku'],
      'gemini': ['gemini-pro']
    }

    return systemModels[provider] || []
  }

  private getAllModelsForProvider(provider: string): string[] {
    const allModels: Record<string, string[]> = {
      'openai': ['gpt-3.5-turbo', 'gpt-4', 'gpt-4-turbo', 'gpt-4o'],
      'anthropic': ['claude-3-haiku', 'claude-3-sonnet', 'claude-3-opus'],
      'gemini': ['gemini-pro', 'gemini-pro-vision', 'gemini-ultra']
    }

    return allModels[provider] || []
  }
}

