// lib/queue/redis-queue.ts
import Redis from 'ioredis'
import { Queue, Worker, Job } from 'bullmq'

interface JobData {
  type: 'formula' | 'bulk'
  userId: string
  request: GenerateRequest | EmbedRequest
  priority: number
  metadata?: any
}

export class AIJobQueue {
  private redis: Redis
  private formulaQueue: Queue<JobData>
  private bulkQueue: Queue<JobData>
  private workers: Worker<JobData>[] = []

  constructor() {
    this.redis = new Redis(process.env.REDIS_URL || 'redis://localhost:6379', {
      retryDelayOnFailover: 100,
      enableReadyCheck: false,
      maxRetriesPerRequest: 3
    })

    this.formulaQueue = new Queue<JobData>('ai-formula', {
      connection: this.redis,
      defaultJobOptions: {
        removeOnComplete: 100,
        removeOnFail: 50,
        attempts: 3,
        backoff: {
          type: 'exponential',
          delay: 1000
        }
      }
    })

    this.bulkQueue = new Queue<JobData>('ai-bulk', {
      connection: this.redis,
      defaultJobOptions: {
        removeOnComplete: 50,
        removeOnFail: 25,
        attempts: 2,
        backoff: {
          type: 'exponential',
          delay: 2000
        }
      }
    })

    this.setupWorkers()
  }

  private setupWorkers() {
    // Formula workers (higher concurrency)
    for (let i = 0; i < 5; i++) {
      const worker = new Worker<JobData>(
        'ai-formula',
        async (job: Job<JobData>) => {
          return await this.processJob(job.data)
        },
        {
          connection: this.redis,
          concurrency: 10,
          limiter: {
            max: 120, // 120 rpm
            duration: 60000
          }
        }
      )

      this.workers.push(worker)
    }

    // Bulk workers (controlled concurrency)
    for (let i = 0; i < 3; i++) {
      const worker = new Worker<JobData>(
        'ai-bulk',
        async (job: Job<JobData>) => {
          return await this.processJob(job.data)
        },
        {
          connection: this.redis,
          concurrency: 5,
          limiter: {
            max: 360, // 360 rpm
            duration: 60000
          }
        }
      )

      this.workers.push(worker)
    }
  }

  async addFormulaJob(
    userId: string,
    request: GenerateRequest | EmbedRequest,
    priority: number = 0
  ): Promise<string> {
    const job = await this.formulaQueue.add(
      'process-formula',
      {
        type: 'formula',
        userId,
        request,
        priority
      },
      {
        priority: priority,
        delay: 0
      }
    )

    return job.id!
  }

  async addBulkJob(
    userId: string,
    request: GenerateRequest | EmbedRequest,
    metadata?: any
  ): Promise<string> {
    const job = await this.bulkQueue.add(
      'process-bulk',
      {
        type: 'bulk',
        userId,
        request,
        priority: 0,
        metadata
      }
    )

    return job.id!
  }

  private async processJob(data: JobData): Promise<GenerateResponse | EmbedResponse> {
    const providerManager = new ProviderManager()

    // Apply rate limiting based on job type
    await this.applyRateLimit(data.userId, data.type)

    try {
      if ('messages' in data.request) {
        return await providerManager.generate(data.request, data.userId)
      } else {
        return await providerManager.embed(data.request, data.userId)
      }
    } catch (error) {
      console.error(`Job processing failed:`, error)
      throw error
    }
  }

  private async applyRateLimit(userId: string, type: 'formula' | 'bulk'): Promise<void> {
    const key = `rate_limit:${type}:${userId}`
    const limit = type === 'formula' ? 120 : 360 // per minute
    const window = 60 // seconds

    const current = await this.redis.incr(key)
    if (current === 1) {
      await this.redis.expire(key, window)
    }

    if (current > limit) {
      const ttl = await this.redis.ttl(key)
      throw new Error(`Rate limit exceeded. Try again in ${ttl} seconds.`)
    }
  }

  async getJobStatus(jobId: string): Promise<any> {
    const formulaJob = await this.formulaQueue.getJob(jobId)
    if (formulaJob) {
      return {
        id: jobId,
        status: await formulaJob.getState(),
        progress: formulaJob.progress,
        result: formulaJob.returnvalue,
        error: formulaJob.failedReason
      }
    }

    const bulkJob = await this.bulkQueue.getJob(jobId)
    if (bulkJob) {
      return {
        id: jobId,
        status: await bulkJob.getState(),
        progress: bulkJob.progress,
        result: bulkJob.returnvalue,
        error: bulkJob.failedReason
      }
    }

    return null
  }

  async shutdown(): Promise<void> {
    await Promise.all(this.workers.map(worker => worker.close()))
    await this.formulaQueue.close()
    await this.bulkQueue.close()
    await this.redis.quit()
  }
}

