import { NextRequest, NextResponse } from 'next/server'
import { authenticateUser } from '@/lib/auth'
import { KMSEncryption } from '@/lib/encryption'

export async function POST(request: NextRequest) {
  try {
    const user = await authenticateUser(request)
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const { provider, apiKey, models } = await request.json()

    // Validate API key format
    if (!apiKey || typeof apiKey !== 'string') {
      return NextResponse.json({ error: 'Invalid API key' }, { status: 400 })
    }

    // Test API key by making a simple request
    try {
      const testConfig = {
        name: `${provider}-test`,
        type: provider,
        apiKey,
        models: { generate: models },
        rateLimits: { rpm: 10 }
      }

      const adapter = ProviderFactory.create(testConfig)
      await adapter.generate({
        messages: [{ role: 'user', content: 'Test' }],
        model: models[0],
        maxTokens: 1
      })
    } catch (error) {
      return NextResponse.json(
        { error: 'Invalid API key or insufficient permissions' },
        { status: 400 }
      )
    }

    // Encrypt and store
    const encryptedKey = KMSEncryption.encryptApiKey(apiKey)
    const keyPreview = `${apiKey.substring(0, 8)}...${apiKey.substring(apiKey.length - 4)}`

    const userApiKey = await db.userApiKey.create({
      data: {
        userId: user.user.id,
        provider,
        encryptedKey,
        keyPreview,
        models,
        isActive: true
      }
    })

    return NextResponse.json({
      id: userApiKey.id,
      provider,
      keyPreview,
      models,
      createdAt: userApiKey.createdAt
    })

  } catch (error) {
    console.error('API key creation error:', error)
    return NextResponse.json(
      { error: 'Failed to add API key' },
      { status: 500 }
    )
  }
}

export async function GET(request: NextRequest) {
  try {
    const user = await authenticateUser(request)
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const userKeys = await db.userApiKey.findMany({
      where: { userId: user.user.id, isActive: true },
      select: {
        id: true,
        provider: true,
        keyPreview: true,
        models: true,
        createdAt: true,
        lastUsed: true
      }
    })

    return NextResponse.json(userKeys)

  } catch (error) {
    console.error('API keys fetch error:', error)
    return NextResponse.json(
      { error: 'Failed to fetch API keys' },
      { status: 500 }
    )
  }
}

