// Example usage in AI function processing
const jobQueue = new AIJobQueue()
const providerManager = new ProviderManager()

async function processAIRequest(
  userId: string,
  functionName: string,
  input: string,
  options: any,
  isFormula: boolean = true
): Promise<{ jobId: string; result?: GenerateResponse }> {

  const request: GenerateRequest = {
    messages: [
      { role: 'user', content: input }
    ],
    model: options.model || 'gpt-3.5-turbo',
    maxTokens: options.maxTokens || 1000,
    temperature: options.temperature || 0.7
  }

  if (isFormula) {
    // High priority for formulas (immediate processing)
    const jobId = await jobQueue.addFormulaJob(userId, request, 10)

    // Wait for immediate result (with timeout)
    const result = await waitForJobResult(jobId, 30000)
    return { jobId, result }
  } else {
    // Bulk processing (queued)
    const jobId = await jobQueue.addBulkJob(userId, request, { functionName })
    return { jobId }
  }
}

async function waitForJobResult(jobId: string, timeoutMs: number): Promise<GenerateResponse> {
  const start = Date.now()

  while (Date.now() - start < timeoutMs) {
    const status = await jobQueue.getJobStatus(jobId)

    if (status?.status === 'completed') {
      return status.result
    } else if (status?.status === 'failed') {
      throw new Error(status.error || 'Job failed')
    }

    await new Promise(resolve => setTimeout(resolve, 100))
  }

  throw new Error('Job timeout')
}


