// lib/web-search/perplexity-adapter.ts
import { BaseProviderAdapter, GenerateRequest, GenerateResponse } from '@/lib/ai-providers/base-adapter'

export interface SearchRequest {
  query: string
  domains?: string[] // up to 3 domains
  recency?: 'hour' | 'day' | 'week' | 'month' | 'year' | 'all'
  contextSize?: 'small' | 'medium' | 'large'
  maxResults?: number
  includeSources?: boolean
  searchType?: 'web' | 'academic' | 'news'
}

export interface SearchResult {
  answer: string
  sources: Array<{
    title: string
    url: string
    snippet: string
    publishedDate?: string
    domain: string
  }>
  usage: {
    inputTokens: number
    outputTokens: number
  }
  searchMetadata: {
    query: string
    resultsCount: number
    searchTime: number
  }
}

export class PerplexitySearchAdapter extends BaseProviderAdapter {
  constructor(apiKey: string) {
    super({
      name: 'perplexity-search',
      type: 'perplexity',
      baseUrl: 'https://api.perplexity.ai',
      apiKey,
      models: {
        generate: ['llama-3.1-sonar-small-128k-online', 'llama-3.1-sonar-large-128k-online', 'llama-3.1-sonar-huge-128k-online']
      },
      rateLimits: { rpm: 20 }
    })
  }

  async search(request: SearchRequest): Promise<SearchResult> {
    const model = this.selectModelByContextSize(request.contextSize || 'medium')
    const searchQuery = this.buildSearchQuery(request)

    const payload = {
      model,
      messages: [
        {
          role: 'system',
          content: this.buildSystemPrompt(request)
        },
        {
          role: 'user',
          content: searchQuery
        }
      ],
      max_tokens: this.getMaxTokensByContextSize(request.contextSize || 'medium'),
      temperature: 0.2,
      return_citations: true,
      return_images: false,
      return_related_questions: false,
      search_domain_filter: request.domains || [],
      search_recency_filter: this.mapRecencyFilter(request.recency || 'all')
    }

    const startTime = Date.now()
    const response = await this.makeRequest('/chat/completions', payload)
    const searchTime = Date.now() - startTime

    return {
      answer: response.choices[0]?.message?.content || '',
      sources: this.extractSources(response.citations || []),
      usage: {
        inputTokens: response.usage?.prompt_tokens || 0,
        outputTokens: response.usage?.completion_tokens || 0
      },
      searchMetadata: {
        query: request.query,
        resultsCount: response.citations?.length || 0,
        searchTime
      }
    }
  }

  private selectModelByContextSize(contextSize: string): string {
    const modelMap = {
      'small': 'llama-3.1-sonar-small-128k-online',
      'medium': 'llama-3.1-sonar-large-128k-online',
      'large': 'llama-3.1-sonar-huge-128k-online'
    }
    return modelMap[contextSize] || modelMap['medium']
  }

  private getMaxTokensByContextSize(contextSize: string): number {
    const tokenMap = {
      'small': 1000,
      'medium': 2000,
      'large': 4000
    }
    return tokenMap[contextSize] || tokenMap['medium']
  }

  private buildSystemPrompt(request: SearchRequest): string {
    let prompt = 'You are a helpful research assistant that provides accurate, well-sourced information.'

    if (request.searchType === 'academic') {
      prompt += ' Focus on academic and scholarly sources. Prefer peer-reviewed content.'
    } else if (request.searchType === 'news') {
      prompt += ' Focus on recent news articles and current events from reputable news sources.'
    }

    prompt += ' Always cite your sources and provide specific, factual information.'

    return prompt
  }

  private buildSearchQuery(request: SearchRequest): string {
    let query = request.query

    // Add domain restrictions if specified
    if (request.domains && request.domains.length > 0) {
      const domainFilter = request.domains.map(d => `site:${d}`).join(' OR ')
      query = `${query} (${domainFilter})`
    }

    return query
  }

  private mapRecencyFilter(recency: string): string {
    const recencyMap = {
      'hour': 'hour',
      'day': 'day',
      'week': 'week',
      'month': 'month',
      'year': 'year',
      'all': ''
    }
    return recencyMap[recency] || ''
  }

  private extractSources(citations: any[]): SearchResult['sources'] {
    return citations.map(citation => ({
      title: citation.title || 'Untitled',
      url: citation.url || '',
      snippet: citation.text || '',
      publishedDate: citation.published_date,
      domain: this.extractDomain(citation.url || '')
    }))
  }

  private extractDomain(url: string): string {
    try {
      return new URL(url).hostname.replace('www.', '')
    } catch {
      return 'unknown'
    }
  }

  async generate(request: GenerateRequest): Promise<GenerateResponse> {
    // Standard generation without search
    const payload = {
      model: request.model,
      messages: request.messages,
      max_tokens: request.maxTokens,
      temperature: request.temperature
    }

    const response = await this.makeRequest('/chat/completions', payload)

    return {
      content: response.choices[0]?.message?.content || '',
      usage: {
        promptTokens: response.usage?.prompt_tokens || 0,
        completionTokens: response.usage?.completion_tokens || 0,
        totalTokens: response.usage?.total_tokens || 0
      },
      model: response.model,
      finishReason: 'stop',
      provider: this.config.name
    }
  }
}

