// lib/web-search/gemini-search-adapter.ts
export class GeminiSearchAdapter {
  constructor(private apiKey: string) {}

  async search(request: SearchRequest): Promise<SearchResult> {
    const searchQuery = this.buildGeminiQuery(request)

    const payload = {
      contents: [
        {
          parts: [
            {
              text: searchQuery
            }
          ]
        }
      ],
      tools: [
        {
          googleSearchRetrieval: {
            dynamicRetrievalConfig: {
              mode: 'MODE_DYNAMIC',
              dynamicThreshold: 0.7
            }
          }
        }
      ],
      generationConfig: {
        maxOutputTokens: this.getMaxTokensByContextSize(request.contextSize || 'medium'),
        temperature: 0.2
      }
    }

    const startTime = Date.now()
    const response = await fetch(
      `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${this.apiKey}`,
      {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      }
    )

    if (!response.ok) {
      throw new Error(`Gemini search failed: ${response.statusText}`)
    }

    const data = await response.json()
    const searchTime = Date.now() - startTime

    return {
      answer: data.candidates?.[0]?.content?.parts?.[0]?.text || '',
      sources: this.extractGeminiSources(data.candidates?.[0]?.groundingMetadata || {}),
      usage: {
        inputTokens: data.usageMetadata?.promptTokenCount || 0,
        outputTokens: data.usageMetadata?.candidatesTokenCount || 0
      },
      searchMetadata: {
        query: request.query,
        resultsCount: data.candidates?.[0]?.groundingMetadata?.groundingChunks?.length || 0,
        searchTime
      }
    }
  }

  private buildGeminiQuery(request: SearchRequest): string {
    let query = `Search for: ${request.query}`

    if (request.domains && request.domains.length > 0) {
      query += `\nLimit search to these domains: ${request.domains.join(', ')}`
    }

    if (request.recency && request.recency !== 'all') {
      query += `\nFocus on content from the past ${request.recency}`
    }

    return query
  }

  private extractGeminiSources(metadata: any): SearchResult['sources'] {
    const chunks = metadata.groundingChunks || []
    return chunks.map((chunk: any) => ({
      title: chunk.web?.title || 'Web Result',
      url: chunk.web?.uri || '',
      snippet: chunk.web?.snippet || '',
      domain: this.extractDomain(chunk.web?.uri || '')
    }))
  }

  private extractDomain(url: string): string {
    try {
      return new URL(url).hostname.replace('www.', '')
    } catch {
      return 'unknown'
    }
  }

  private getMaxTokensByContextSize(contextSize: string): number {
    const tokenMap = {
      'small': 1000,
      'medium': 2000,
      'large': 4000
    }
    return tokenMap[contextSize] || tokenMap['medium']
  }
}

