// lib/web-search/search-service.ts
export class WebSearchService {
  private perplexityAdapter: PerplexitySearchAdapter | null = null
  private geminiAdapter: GeminiSearchAdapter | null = null

  constructor() {
    if (process.env.PERPLEXITY_API_KEY) {
      this.perplexityAdapter = new PerplexitySearchAdapter(process.env.PERPLEXITY_API_KEY)
    }

    if (process.env.GEMINI_API_KEY) {
      this.geminiAdapter = new GeminiSearchAdapter(process.env.GEMINI_API_KEY)
    }
  }

  async search(request: SearchRequest, provider: 'perplexity' | 'gemini' = 'perplexity'): Promise<SearchResult> {
    // Validate domain limit
    if (request.domains && request.domains.length > 3) {
      throw new Error('Maximum 3 domains allowed for domain targeting')
    }

    // Validate query
    if (!request.query || request.query.trim().length === 0) {
      throw new Error('Search query cannot be empty')
    }

    try {
      switch (provider) {
        case 'perplexity':
          if (!this.perplexityAdapter) {
            throw new Error('Perplexity API key not configured')
          }
          return await this.perplexityAdapter.search(request)

        case 'gemini':
          if (!this.geminiAdapter) {
            throw new Error('Gemini API key not configured')
          }
          return await this.geminiAdapter.search(request)

        default:
          throw new Error(`Unsupported search provider: ${provider}`)
      }
    } catch (error) {
      console.error(`Web search failed (${provider}):`, error)
      throw error
    }
  }

  async multiProviderSearch(request: SearchRequest): Promise<{
    primary: SearchResult
    fallback?: SearchResult
    provider: string
  }> {
    // Try Perplexity first, fallback to Gemini
    try {
      if (this.perplexityAdapter) {
        const result = await this.perplexityAdapter.search(request)
        return { primary: result, provider: 'perplexity' }
      }
    } catch (error) {
      console.warn('Perplexity search failed, trying Gemini:', error)
    }

    // Fallback to Gemini
    if (this.geminiAdapter) {
      const result = await this.geminiAdapter.search(request)
      return { primary: result, provider: 'gemini' }
    }

    throw new Error('No search providers available')
  }
}

