const APPS_SCRIPT_SCRAPER_CODE = `
/**
 * AI Sheets Web Scraper v2.0
 * Enhanced scraping with better content extraction
 * Free tool - not billed to credit packs
 */

// Main scraping function
function SCRAPE(url, options = {}) {
  try {
    if (!url || typeof url !== 'string') {
      return 'Error: Invalid URL provided';
    }

    // Normalize URL
    if (!url.startsWith('http://') && !url.startsWith('https://')) {
      url = 'https://' + url;
    }

    const defaultOptions = {
      timeout: 10000,
      followRedirects: true,
      extractImages: false,
      extractLinks: false,
      maxLength: 50000,
      selector: null // CSS selector for specific content
    };

    const config = { ...defaultOptions, ...options };

    // Fetch page content
    const response = UrlFetchApp.fetch(url, {
      method: 'GET',
      headers: {
        'User-Agent': 'Mozilla/5.0 (compatible; AI-Sheets-Scraper/2.0; +https://aisheets.com/bot)',
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
        'Accept-Language': 'en-US,en;q=0.5',
        'Accept-Encoding': 'gzip, deflate',
        'Connection': 'keep-alive'
      },
      followRedirects: config.followRedirects,
      muteHttpExceptions: true
    });

    const responseCode = response.getResponseCode();

    if (responseCode === 403) {
      return 'Error: Access forbidden (403). Site may block automated requests.';
    } else if (responseCode === 404) {
      return 'Error: Page not found (404).';
    } else if (responseCode !== 200) {
      return \`Error: HTTP \${responseCode}\`;
    }

    const html = response.getContentText();
    const contentType = response.getHeaders()['Content-Type'] || '';

    // Check if it's HTML content
    if (!contentType.toLowerCase().includes('text/html')) {
      return 'Error: Non-HTML content type detected';
    }

    // Extract text content
    let textContent = extractTextFromHtml(html, config);

    // Apply length limit
    if (textContent.length > config.maxLength) {
      textContent = textContent.substring(0, config.maxLength) + '... [Content truncated to 50k chars]';
    }

    return textContent;

  } catch (error) {
    if (error.message.includes('timeout')) {
      return 'Error: Request timeout. Site may be slow to respond.';
    } else if (error.message.includes('DNS')) {
      return 'Error: Domain not found or DNS resolution failed.';
    } else {
      return \`Error: \${error.message}\`;
    }
  }
}

// Enhanced text extraction
function extractTextFromHtml(html, config) {
  // Remove script and style tags completely
  html = html.replace(/<script[^>]*>.*?<\\/script>/gis, '');
  html = html.replace(/<style[^>]*>.*?<\\/style>/gis, '');
  html = html.replace(/<noscript[^>]*>.*?<\\/noscript>/gis, '');
  html = html.replace(/<!--.*?-->/gis, '');

  // Remove common navigation and footer elements
  html = html.replace(/<nav[^>]*>.*?<\\/nav>/gis, '');
  html = html.replace(/<footer[^>]*>.*?<\\/footer>/gis, '');
  html = html.replace(/<header[^>]*>.*?<\\/header>/gis, '');

  // If selector is provided, try to extract specific content
  if (config.selector) {
    // Simple selector support (this is basic - real CSS parsing would be complex)
    const matches = html.match(new RegExp(\`<[^>]*class="[^"]*\${config.selector}[^"]*"[^>]*>(.*?)</[^>]*>\`, 'is'));
    if (matches && matches[1]) {
      html = matches[1];
    }
  }

  // Convert common HTML entities
  const entityMap = {
    '&nbsp;': ' ',
    '&amp;': '&',
    '&lt;': '<',
    '&gt;': '>',
    '&quot;': '"',
    '&#39;': "'",
    '&apos;': "'",
    '&cent;': '',
    '&pound;': '',
    '&yen;': '',
    '&euro;': '',
    '&copy;': '',
    '&reg;': '',
    '&trade;': ''
  };

  // Replace HTML entities
  Object.keys(entityMap).forEach(entity => {
    html = html.replace(new RegExp(entity, 'g'), entityMap[entity]);
  });

  // Remove all HTML tags
  let textContent = html.replace(/<[^>]+>/g, ' ');

  // Clean up whitespace
  textContent = textContent
    .replace(/\\s+/g, ' ')
    .replace(/\\n\\s*\\n/g, '\\n')
    .trim();

  // Remove empty lines and excessive spacing
  textContent = textContent
    .split('\\n')
    .map(line => line.trim())
    .filter(line => line.length > 0)
    .join('\\n');

  return textContent;
}

// Batch scraping function
function SCRAPE_BATCH(urlRange, options = {}) {
  if (!Array.isArray(urlRange)) {
    return SCRAPE(urlRange, options);
  }

  return urlRange.map(row => {
    const url = Array.isArray(row) ? row[0] : row;

    if (!url) {
      return 'Error: Empty URL';
    }

    try {
      return SCRAPE(url, options);
    } catch (error) {
      return \`Error: \${error.message}\`;
    }
  });
}

// Extract specific elements
function SCRAPE_ELEMENT(url, selector) {
  return SCRAPE(url, { selector: selector });
}

// Extract all links from a page
function SCRAPE_LINKS(url, filterDomain = null) {
  try {
    const response = UrlFetchApp.fetch(url, {
      method: 'GET',
      headers: {
        'User-Agent': 'Mozilla/5.0 (compatible; AI-Sheets-Scraper/2.0)'
      }
    });

    if (response.getResponseCode() !== 200) {
      return 'Error: Failed to fetch page';
    }

    const html = response.getContentText();
    const linkRegex = /<a[^>]+href="([^"]+)"[^>]*>([^<]*)</gi;
    const links = [];
    let match;

    while ((match = linkRegex.exec(html)) !== null) {
      const href = match[1];
      const text = match[2].trim();

      // Skip empty links or anchors
      if (!href || href.startsWith('#') || href.startsWith('javascript:')) {
        continue;
      }

      // Convert relative URLs to absolute
      let absoluteUrl = href;
      if (href.startsWith('/')) {
        const baseUrl = new URL(url);
        absoluteUrl = \`\${baseUrl.protocol}//\${baseUrl.host}\${href}\`;
      } else if (!href.startsWith('http')) {
        absoluteUrl = new URL(href, url).href;
      }

      // Filter by domain if specified
      if (filterDomain) {
        if (!absoluteUrl.includes(filterDomain)) {
          continue;
        }
      }

      links.push(\`\${text} | \${absoluteUrl}\`);
    }

    return links.slice(0, 100).join('\\n'); // Limit to 100 links

  } catch (error) {
    return \`Error: \${error.message}\`;
  }
}

// Test function for Apps Script editor
function testScraper() {
  const result = SCRAPE('https://example.com');
  console.log(result);
}
`;

