import { NextRequest, NextResponse } from 'next/server'
import { authenticateUser } from '@/lib/auth'
import { WebSearchService } from '@/lib/web-search/search-service'
import { deductCreditsAtomic } from '@/lib/transactions'
import { getCurrentCreditBalance } from '@/lib/db'

const searchService = new WebSearchService()
const SEARCH_CREDIT_COST = 3 // Higher cost for web search

export async function POST(request: NextRequest) {
  let usageEventId: string | null = null
  const startTime = Date.now()

  try {
    const user = await authenticateUser(request)
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Check credit balance
    const currentBalance = await getCurrentCreditBalance(user.user.id)
    if (currentBalance < SEARCH_CREDIT_COST) {
      return NextResponse.json({ 
        error: 'Insufficient credits',
        required: SEARCH_CREDIT_COST,
        available: currentBalance
      }, { status: 402 })
    }

    const body = await request.json()
    const {
      query,
      domains = [],
      recency = 'all',
      contextSize = 'medium',
      maxResults = 5,
      includeSources = true,
      searchType = 'web',
      provider = 'perplexity'
    } = body

    // Validate request
    if (!query || typeof query !== 'string') {
      return NextResponse.json({ error: 'Invalid search query' }, { status: 400 })
    }

    if (domains.length > 3) {
      return NextResponse.json({ error: 'Maximum 3 domains allowed' }, { status: 400 })
    }

    // Create usage event
    const usageEvent = await db.usageEvent.create({
      data: {
        userId: user.user.id,
        functionName: 'WEB_SEARCH',
        creditsUsed: SEARCH_CREDIT_COST,
        status: 'pending',
        ipAddress: request.ip || 'unknown',
        userAgent: request.headers.get('user-agent'),
        metadata: JSON.stringify({ 
          query: query.substring(0, 100),
          provider,
          domains,
          contextSize
        })
      }
    })
    usageEventId = usageEvent.id

    // Perform search
    const searchRequest = {
      query,
      domains,
      recency,
      contextSize,
      maxResults,
      includeSources,
      searchType
    }

    const searchResult = await searchService.search(searchRequest, provider)

    // Deduct credits
    await deductCreditsAtomic(user.user.id, SEARCH_CREDIT_COST, usageEventId, {
      provider,
      query: query.substring(0, 100),
      resultsCount: searchResult.sources.length
    })

    // Update usage event
    await db.usageEvent.update({
      where: { id: usageEventId },
      data: {
        status: 'success',
        processingMs: Date.now() - startTime,
        inputTokens: searchResult.usage.inputTokens,
        outputTokens: searchResult.usage.outputTokens
      }
    })

    return NextResponse.json({
      answer: searchResult.answer,
      sources: includeSources ? searchResult.sources : undefined,
      metadata: {
        searchTime: searchResult.searchMetadata.searchTime,
        resultsCount: searchResult.searchMetadata.resultsCount,
        provider,
        creditsUsed: SEARCH_CREDIT_COST,
        remainingCredits: currentBalance - SEARCH_CREDIT_COST
      },
      usage: searchResult.usage
    })

  } catch (error) {
    // Update usage event with error
    if (usageEventId) {
      await db.usageEvent.update({
        where: { id: usageEventId },
        data: {
          status: 'error',
          errorMessage: error instanceof Error ? error.message : 'Unknown error',
          processingMs: Date.now() - startTime
        }
      }).catch(console.error)
    }

    console.error('Web search error:', error)
    return NextResponse.json({ 
      error: 'Search failed',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 })
  }
}

