// Integration with existing bulk tools system
export async function processWebSearchBulk(
  userId: string,
  inputData: Array<Record<string, any>>,
  searchConfig: {
    queryColumn: string
    domains?: string[]
    recency?: string
    contextSize?: string
    includeSources?: boolean
  }
): Promise<Array<{
  query: string
  result: string
  sources?: string[]
  error?: string
  creditsUsed: number
}>> {

  const searchService = new WebSearchService()
  const results = []

  for (const row of inputData) {
    try {
      const query = row[searchConfig.queryColumn]
      if (!query) {
        results.push({
          query: '',
          result: '',
          error: 'Empty query',
          creditsUsed: 0
        })
        continue
      }

      const searchRequest = {
        query: String(query),
        domains: searchConfig.domains,
        recency: searchConfig.recency || 'all',
        contextSize: searchConfig.contextSize || 'medium',
        includeSources: searchConfig.includeSources
      }

      const searchResult = await searchService.search(searchRequest)

      results.push({
        query: searchRequest.query,
        result: searchResult.answer,
        sources: searchConfig.includeSources ? 
          searchResult.sources.map(s => s.url) : undefined,
        creditsUsed: SEARCH_CREDIT_COST
      })

      // Rate limiting delay for bulk operations
      await new Promise(resolve => setTimeout(resolve, 1000))

    } catch (error) {
      results.push({
        query: row[searchConfig.queryColumn] || '',
        result: '',
        error: error instanceof Error ? error.message : 'Search failed',
        creditsUsed: 0
      })
    }
  }

  return results
}

