@app.post("/api/v1/generate")
async def generate_text(
    request: GenerateRequest,
    background_tasks: BackgroundTasks,
    credentials: HTTPAuthorizationCredentials = Depends(security)
):
    # Authenticate user
    user = await auth_manager.authenticate_user(credentials.credentials)
    if not user:
        raise HTTPException(status_code=401, detail="Invalid credentials")

    # Check cache first
    cache_key = cache_manager.generate_cache_key(
        function="generate",
        inputs=request.messages,
        model=request.model,
        temperature=request.temperature,
        user_id=user.id
    )

    cached_result = await cache_manager.get(cache_key)
    if cached_result:
        return GenerateResponse(**cached_result, cached=True)

    # Check rate limits and credits
    await job_manager.check_rate_limit(user.id, "formula" if request.priority > 0 else "bulk")
    await auth_manager.check_credits(user.id, get_function_cost(request.function_name))

    # Submit to job queue
    job_id = await job_manager.submit_job(
        user_id=user.id,
        job_type="generate",
        data=request.dict(),
        priority=request.priority
    )

    # For formulas (high priority), wait for result
    if request.priority > 0:
        result = await job_manager.wait_for_result(job_id, timeout=30)

        # Cache the result
        background_tasks.add_task(
            cache_manager.set,
            cache_key,
            result.dict(),
            ttl=30 * 24 * 60 * 60  # 30 days
        )

        return result

    # For bulk operations, return job ID
    return {"job_id": job_id, "status": "queued"}

@app.post("/api/v1/embed")
