// Custom Functions Implementation
function GPT(prompt, model, temperature) {
  return callAIFunction('GPT', {
    prompt: prompt,
    model: model || 'gpt-3.5-turbo',
    temperature: temperature || 0.7
  });
}

function GPT_EDIT(text, instruction, model, temperature) {
  return callAIFunction('GPT_EDIT', {
    text: text,
    instruction: instruction || 'Improve the text',
    model: model || 'gpt-3.5-turbo',
    temperature: temperature || 0.3
  });
}

function GPT_TRANSLATE(text, targetLanguage, model) {
  return callAIFunction('GPT_TRANSLATE', {
    text: text,
    targetLanguage: targetLanguage || 'Spanish',
    model: model || 'gpt-3.5-turbo'
  });
}

function GPT_CLASSIFY(text, categories, model) {
  if (!categories) {
    categories = ['Positive', 'Neutral', 'Negative'];
  }

  return callAIFunction('GPT_CLASSIFY', {
    text: text,
    categories: Array.isArray(categories) ? categories : categories.split(','),
    model: model || 'gpt-3.5-turbo'
  });
}

function GPT_EXTRACT(text, fields, format, model) {
  return callAIFunction('GPT_EXTRACT', {
    text: text,
    fields: Array.isArray(fields) ? fields : fields.split(','),
    format: format || 'json',
    model: model || 'gpt-3.5-turbo'
  });
}

function GPT_SUMMARIZE(text, maxLength, style, model) {
  return callAIFunction('GPT_SUMMARIZE', {
    text: text,
    maxLength: maxLength || 100,
    style: style || 'concise',
    model: model || 'gpt-3.5-turbo'
  });
}

function GPT_LIST(prompt, count, style, model) {
  return callAIFunction('GPT_LIST', {
    prompt: prompt,
    count: count || 5,
    style: style || 'numbered',
    model: model || 'gpt-3.5-turbo'
  });
}

function GPT_TABLE(prompt, columns, rows, model) {
  return callAIFunction('GPT_TABLE', {
    prompt: prompt,
    columns: Array.isArray(columns) ? columns : columns.split(','),
    rows: rows || 3,
    model: model || 'gpt-3.5-turbo'
  });
}

function GPT_VISION(imageUrls, prompt, model) {
  // Support up to 5 image URLs
  var images = [];
  if (typeof imageUrls === 'string') {
    images = [imageUrls];
  } else if (Array.isArray(imageUrls)) {
