  });
}

function GPT_MATCH(query, dataset, threshold, maxResults) {
  // Cap dataset at 1000 rows
  var data = Array.isArray(dataset) ? dataset.slice(0, 1000) : [dataset];

  return callAIFunction('GPT_MATCH', {
    query: query,
    dataset: data,
    threshold: threshold || 0.8,
    maxResults: maxResults || 5
  });
}

// Core function caller with caching
function callAIFunction(functionName, params) {
  try {
    var cacheKey = generateCacheKey(functionName, params);
    var cache = CacheService.getUserCache();
    var cached = cache.get(cacheKey);

    if (cached) {
      var result = JSON.parse(cached);
      return result.output;
    }

    var apiUrl = getApiUrl();
    var token = getUserToken();

    var payload = {
      function: functionName,
      parameters: params,
      priority: 10, // High priority for formula calls
      cache: true
    };

    var options = {
      method: 'POST',
      headers: {
        'Authorization': 'Bearer ' + token,
        'Content-Type': 'application/json'
      },
      payload: JSON.stringify(payload)
    };

    var response = UrlFetchApp.fetch(apiUrl + '/api/v1/generate', options);
    var responseData = JSON.parse(response.getContentText());

    if (response.getResponseCode() !== 200) {
      throw new Error(responseData.detail || 'API request failed');
    }

    // Cache for 30 days (max Apps Script cache time is 6 hours, so use Properties)
    if (!responseData.cached) {
      PropertiesService.getUserProperties().setProperty(
        'cache_' + cacheKey, 
        JSON.stringify({
          output: responseData.content,
          timestamp: new Date().getTime()
        })
      );
    }

    return responseData.content;

  } catch (error) {
    console.error('AI Function Error:', error);
    return 'Error: ' + error.toString();
  }
}

function generateCacheKey(functionName, params) {
  var keyData = {
    function: functionName,
    params: params,
    user: Session.getActiveUser().getEmail()
  };

  var keyString = JSON.stringify(keyData);
  return Utilities.computeDigest(Utilities.DigestAlgorithm.SHA_256, keyString)
    .map(function(byte) { return (byte + 256).toString(16).slice(1); })
    .join('');
}

// Clean up old cache entries
function cleanupCache() {
  var properties = PropertiesService.getUserProperties();
  var allProps = properties.getProperties();
  var now = new Date().getTime();
  var thirtyDays = 30 * 24 * 60 * 60 * 1000;

  Object.keys(allProps).forEach(function(key) {
    if (key.startsWith('cache_')) {
      try {
        var cached = JSON.parse(allProps[key]);
        if (now - cached.timestamp > thirtyDays) {
          properties.deleteProperty(key);
        }
      } catch (e) {
        // Delete corrupted cache entries
        properties.deleteProperty(key);
      }
    }
  });
}

