    </div>
  </div>

  <!-- Action Buttons -->
  <div class="flex" style="margin-bottom: 16px;">
    <button class="button flex-1" id="run-button">Run</button>
    <button class="button-secondary" id="pause-button" disabled>Pause</button>
  </div>

  <!-- Progress Section -->
  <div id="progress-section" class="form-section hidden">
    <div class="progress-bar">
      <div class="progress-fill" id="progress-fill" style="width: 0%"></div>
    </div>
    <div class="status-text" id="status-text">Ready to run...</div>
    <div class="results-area" id="results-area"></div>
  </div>

  <!-- History Section -->
  <div class="form-section">
    <div class="flex" style="margin-bottom: 12px;">
      <div class="label" style="margin-bottom: 0;">Recent Jobs (90 days)</div>
      <button class="button-secondary" id="refresh-history" style="margin-left: auto; padding: 4px 8px; font-size: 12px;">Refresh</button>
    </div>
    <div id="history-list">
      <!-- History items will be populated here -->
    </div>
  </div>

  <script>
    let currentTool = null;
    let currentJobId = null;
    let isRunning = false;

    // Initialize
    document.addEventListener('DOMContentLoaded', function() {
      setupEventListeners();
      loadHistory();
    });

    function setupEventListeners() {
      // Tool selection
      document.querySelectorAll('.tool-card').forEach(card => {
        card.addEventListener('click', function() {
          selectTool(this.dataset.tool);
        });
      });

      // Action buttons
      document.getElementById('run-button').addEventListener('click', runBulkOperation);
      document.getElementById('pause-button').addEventListener('click', pauseOperation);
      document.getElementById('refresh-history').addEventListener('click', loadHistory);
    }

    function selectTool(toolName) {
      currentTool = toolName;

      // Update UI
      document.querySelectorAll('.tool-card').forEach(card => {
        card.classList.remove('active');
      });
      document.querySelector(`[data-tool="${toolName}"]`).classList.add('active');

      // Show appropriate form
      document.querySelectorAll('.form-section').forEach(form => {
        if (form.id === `${toolName}-form`) {
          form.classList.remove('hidden');
        } else if (form.id.endsWith('-form')) {
          form.classList.add('hidden');
        }
      });
    }

    async function runBulkOperation() {
      if (!currentTool) {
        showError('Please select a tool first');
        return;
      }

      try {
        isRunning = true;
        updateUI();

        const config = collectFormData();
        const response = await callBackendAPI('/api/v1/bulk/create', {
          method: 'POST',
          body: JSON.stringify({
            tool: currentTool,
            config: config
          })
        });

        if (response.job_id) {
          currentJobId = response.job_id;
          monitorJob(response.job_id);
        } else {
          // Immediate result
          displayResults(response);
          isRunning = false;
          updateUI();
        }

      } catch (error) {
        showError('Failed to start operation: ' + error.message);
        isRunning = false;
        updateUI();
      }
    }

    function collectFormData() {
      const base = {
        customInstructions: document.getElementById('custom-instructions').value
      };

      switch (currentTool) {
        case 'custom':
          return {
            ...base,
            inputColumn: document.getElementById('custom-input-column').value,
            prompt: document.getElementById('custom-prompt').value,
            outputColumn: document.getElementById('custom-output-column').value,
            model: document.getElementById('custom-model').value
          };

        case 'translate':
          return {
            ...base,
            inputColumn: document.getElementById('translate-input-column').value,
            targetLanguage: document.getElementById('translate-language').value,
            outputColumn: document.getElementById('translate-output-column').value
          };

        case 'websearch':
          return {
            ...base,
            queryColumn: document.getElementById('websearch-query-column').value,
            domains: document.getElementById('websearch-domains').value.split(',').map(d => d.trim()).filter(Boolean),
            recency: document.getElementById('websearch-recency').value,
            resultColumn: document.getElementById('websearch-result-column').value,
            sourcesColumn: document.getElementById('websearch-sources-column').value
          };

        // Add other tools...
      }
    }

    async function monitorJob(jobId) {
      const interval = setInterval(async () => {
        try {
          const status = await callBackendAPI(`/api/v1/jobs/${jobId}`);

          updateProgress(status.progress || 0, status.status || 'processing');

          if (status.status === 'completed') {
            clearInterval(interval);
            displayResults(status.result);
            isRunning = false;
            updateUI();
            loadHistory();
          } else if (status.status === 'failed') {
            clearInterval(interval);
            showError('Job failed: ' + status.error);
            isRunning = false;
            updateUI();
          }
        } catch (error) {
          console.error('Error monitoring job:', error);
        }
      }, 2000);
    }

    function updateProgress(progress, status) {
      document.getElementById('progress-section').classList.remove('hidden');
      document.getElementById('progress-fill').style.width = `${progress}%`;
      document.getElementById('status-text').textContent = status;
    }

    function displayResults(result) {
      const resultsArea = document.getElementById('results-area');
      resultsArea.textContent = JSON.stringify(result, null, 2);
    }

    function updateUI() {
      document.getElementById('run-button').disabled = isRunning;
      document.getElementById('pause-button').disabled = !isRunning;
    }

    async function callBackendAPI(endpoint, options = {}) {
      const apiUrl = '<?= apiUrl ?>';
      const token = '<?= userToken ?>';

      const response = await fetch(apiUrl + endpoint, {
        headers: {
          'Authorization': 'Bearer ' + token,
          'Content-Type': 'application/json',
          ...options.headers
        },
        ...options
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.detail || 'API request failed');
      }

      return await response.json();
    }

    function showError(message) {
      // Simple error display - could be enhanced with better UI
      alert('Error: ' + message);
    }

    async function loadHistory() {
      try {
        const history = await callBackendAPI('/api/v1/bulk/history');
        const historyList = document.getElementById('history-list');

        historyList.innerHTML = history.map(job => `
          <div class="history-item" onclick="loadHistoryJob('${job.id}')">
            <div class="history-title">${job.tool} - ${job.status}</div>
            <div class="history-meta">${new Date(job.created_at).toLocaleDateString()}  ${job.rows_processed || 0} rows</div>
          </div>
        `).join('');

      } catch (error) {
        console.error('Failed to load history:', error);
      }
    }

    function loadHistoryJob(jobId) {
      // Load job configuration for reuse
      // Implementation would populate form fields with saved job data
    }

    // Auto-cleanup cache periodically
    google.script.run.cleanupCache();
  </script>
</body>
</html>

