<!-- formula-assistant.html -->
<!DOCTYPE html>
<html>
<head>
  <base target="_top">
  <meta charset="utf-8">
  <title>Formula Assistant</title>
  <style>
    /* Similar styling to main sidebar */
    body {
      font-family: 'Google Sans', Roboto, sans-serif;
      margin: 0;
      padding: 16px;
      background: #f8f9fa;
      font-size: 14px;
    }

    .chat-container {
      height: 400px;
      background: white;
      border: 1px solid #dadce0;
      border-radius: 8px;
      display: flex;
      flex-direction: column;
    }

    .chat-messages {
      flex: 1;
      overflow-y: auto;
      padding: 16px;
    }

    .message {
      margin-bottom: 16px;
      padding: 12px;
      border-radius: 8px;
      max-width: 90%;
    }

    .user-message {
      background: #e3f2fd;
      margin-left: auto;
      text-align: right;
    }

    .assistant-message {
      background: #f5f5f5;
      margin-right: auto;
    }

    .chat-input {
      border-top: 1px solid #e8eaed;
      padding: 12px 16px;
      display: flex;
      gap: 8px;
    }

    .chat-input input {
      flex: 1;
      padding: 8px 12px;
      border: 1px solid #dadce0;
      border-radius: 20px;
      outline: none;
    }

    .chat-input button {
      background: #4285f4;
      color: white;
      border: none;
      border-radius: 20px;
      padding: 8px 16px;
      cursor: pointer;
    }

    .formula-examples {
      background: white;
      border: 1px solid #dadce0;
      border-radius: 8px;
      padding: 16px;
      margin-top: 16px;
    }

    .example-formula {
      background: #f8f9fa;
      border: 1px solid #e8eaed;
      border-radius: 4px;
      padding: 8px 12px;
      font-family: monospace;
      font-size: 12px;
      margin: 8px 0;
      cursor: pointer;
    }

    .example-formula:hover {
      background: #e3f2fd;
    }
  </style>
</head>
<body>
  <div class="header">
    <div class="logo"></div>
    <div class="title">Formula Assistant</div>
  </div>

  <div class="chat-container">
    <div class="chat-messages" id="chat-messages">
      <div class="assistant-message message">
        Hi! I can help you create and explain Google Sheets formulas. Try asking me:
        <ul>
          <li>"Create a formula to sum values in column A"</li>
          <li>"How do I count unique values?"</li>
          <li>"Explain this formula: =VLOOKUP(A2,B:C,2,0)"</li>
        </ul>
      </div>
    </div>

    <div class="chat-input">
      <input type="text" id="message-input" placeholder="Ask about formulas..." onkeypress="handleKeyPress(event)">
      <button onclick="sendMessage()">Send</button>
    </div>
  </div>

  <div class="formula-examples">
    <div class="label">Quick Examples:</div>
    <div class="example-formula" onclick="insertExample(this.textContent)">
      =GPT("Summarize this text", A1)
    </div>
    <div class="example-formula" onclick="insertExample(this.textContent)">
      =GPT_TRANSLATE(A1, "Spanish")
    </div>
    <div class="example-formula" onclick="insertExample(this.textContent)">
      =GPT_CLASSIFY(A1, "Positive,Neutral,Negative")
    </div>
    <div class="example-formula" onclick="insertExample(this.textContent)">
      =GPT_EXTRACT(A1, "Name,Email,Phone")
    </div>
  </div>

  <script>
    let conversationHistory = [];

    function handleKeyPress(event) {
      if (event.key === 'Enter') {
        sendMessage();
      }
    }

    async function sendMessage() {
      const input = document.getElementById('message-input');
      const message = input.value.trim();

      if (!message) return;

      // Add user message to chat
      addMessage(message, 'user');
      input.value = '';

      // Add conversation to history
      conversationHistory.push({ role: 'user', content: message });

      try {
        // Call AI for response
        const response = await callFormulaAssistant(message);
        addMessage(response, 'assistant');
        conversationHistory.push({ role: 'assistant', content: response });

      } catch (error) {
        addMessage('Sorry, I encountered an error. Please try again.', 'assistant');
        console.error('Formula assistant error:', error);
      }
    }

    function addMessage(content, sender) {
      const messagesContainer = document.getElementById('chat-messages');
      const messageDiv = document.createElement('div');
      messageDiv.className = `message ${sender}-message`;
      messageDiv.textContent = content;

      messagesContainer.appendChild(messageDiv);
      messagesContainer.scrollTop = messagesContainer.scrollHeight;
    }

    async function callFormulaAssistant(userMessage) {
      // This would call your backend's formula assistant endpoint
      const apiUrl = '<?= apiUrl ?>';
      const token = '<?= userToken ?>';

      const response = await fetch(apiUrl + '/api/v1/formula-assistant', {
        method: 'POST',
        headers: {
          'Authorization': 'Bearer ' + token,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          message: userMessage,
          history: conversationHistory.slice(-10) // Keep last 10 messages
        })
      });

      if (!response.ok) {
        throw new Error('Formula assistant request failed');
      }

      const data = await response.json();
      return data.response;
    }

    function insertExample(formula) {
      const input = document.getElementById('message-input');
      input.value = `Explain this formula: ${formula}`;
      sendMessage();
    }
  </script>
</body>
</html>


