interface ApiKeyModalProps {
  onClose: () => void
  onSave: () => void
}

function ApiKeyModal({ onClose, onSave }: ApiKeyModalProps) {
  const [provider, setProvider] = useState('openai')
  const [apiKey, setApiKey] = useState('')
  const [keyName, setKeyName] = useState('')
  const [isValidating, setIsValidating] = useState(false)
  const [validationResult, setValidationResult] = useState<{
    valid: boolean
    models?: string[]
    error?: string
  } | null>(null)

  const validateApiKey = async () => {
    if (!apiKey.trim()) return

    setIsValidating(true)
    setValidationResult(null)

    try {
      const response = await fetch('/api/user/api-keys/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          provider,
          apiKey: apiKey.trim()
        })
      })

      const result = await response.json()

      if (response.ok) {
        setValidationResult({
          valid: true,
          models: result.models || []
        })
      } else {
        setValidationResult({
          valid: false,
          error: result.error || 'Validation failed'
        })
      }
    } catch (error) {
      setValidationResult({
        valid: false,
        error: 'Network error during validation'
      })
    } finally {
      setIsValidating(false)
    }
  }

  const saveApiKey = async () => {
    if (!validationResult?.valid) return

    try {
      const response = await fetch('/api/user/api-keys', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          provider,
          apiKey: apiKey.trim(),
          name: keyName.trim() || `${provider} API Key`,
          models: validationResult.models || []
        })
      })

      if (response.ok) {
        onSave()
        onClose()
      } else {
        const error = await response.json()
        alert('Failed to save API key: ' + error.error)
      }
    } catch (error) {
      alert('Failed to save API key: Network error')
    }
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md">
        <h2 className="text-lg font-semibold mb-4">Add API Key</h2>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Provider</label>
            <select
              value={provider}
              onChange={(e) => {
                setProvider(e.target.value)
                setValidationResult(null)
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="openai">OpenAI</option>
              <option value="anthropic">Anthropic</option>
              <option value="google">Google</option>
              <option value="perplexity">Perplexity</option>
              <option value="openrouter">OpenRouter</option>
              <option value="deepseek">DeepSeek</option>
              <option value="xai">xAI</option>
              <option value="mistral">Mistral</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Key Name (optional)</label>
            <input
              type="text"
              value={keyName}
              onChange={(e) => setKeyName(e.target.value)}
              placeholder={`My ${provider} key`}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">API Key</label>
            <input
              type="password"
              value={apiKey}
              onChange={(e) => {
                setApiKey(e.target.value)
                setValidationResult(null)
              }}
              placeholder="sk-..."
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <div className="flex justify-between mt-2">
              <Button
                variant="outline"
                size="sm"
                onClick={validateApiKey}
                disabled={!apiKey.trim() || isValidating}
              >
                {isValidating ? 'Validating...' : 'Test Key'}
              </Button>
            </div>
          </div>

          {validationResult && (
            <div className={`p-3 rounded-lg ${
              validationResult.valid 
                ? 'bg-green-50 border border-green-200' 
                : 'bg-red-50 border border-red-200'
            }`}>
              <div className="flex items-center space-x-2">
                {validationResult.valid ? (
                  <Check className="w-4 h-4 text-green-600" />
                ) : (
                  <X className="w-4 h-4 text-red-600" />
                )}
                <span className={`text-sm font-medium ${
                  validationResult.valid ? 'text-green-800' : 'text-red-800'
                }`}>
                  {validationResult.valid 
                    ? `Valid! Found ${validationResult.models?.length || 0} models`
                    : validationResult.error
                  }
                </span>
              </div>
              {validationResult.valid && validationResult.models && (
                <div className="mt-2 text-xs text-green-700">
                  Models: {validationResult.models.slice(0, 3).join(', ')}
                  {validationResult.models.length > 3 && ` +${validationResult.models.length - 3} more`}
                </div>
              )}
            </div>
          )}

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <Shield className="w-4 h-4 text-blue-600" />
              <span className="text-sm font-medium text-blue-800">Zero Retention Mode</span>
            </div>
            <p className="text-xs text-blue-700 mt-1">
              With your API key, inputs and outputs are not logged or stored by our system.
            </p>
          </div>
        </div>

        <div className="flex space-x-3 mt-6">
          <Button
            onClick={saveApiKey}
            disabled={!validationResult?.valid}
            className="flex-1"
          >
            Save API Key
          </Button>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  )
}

