interface CustomEndpointModalProps {
  onClose: () => void
  onSave: () => void
}

function CustomEndpointModal({ onClose, onSave }: CustomEndpointModalProps) {
  const [endpointType, setEndpointType] = useState<'azure' | 'ollama' | 'custom'>('custom')
  const [name, setName] = useState('')
  const [baseUrl, setBaseUrl] = useState('')
  const [apiKey, setApiKey] = useState('')
  const [azureDeployment, setAzureDeployment] = useState('')
  const [azureApiVersion, setAzureApiVersion] = useState('2024-02-15-preview')
  const [isHealthChecking, setIsHealthChecking] = useState(false)
  const [healthResult, setHealthResult] = useState<{
    healthy: boolean
    models?: string[]
    error?: string
  } | null>(null)

  const buildUrl = () => {
    switch (endpointType) {
      case 'azure':
        return `${baseUrl}/openai/deployments/${azureDeployment}/chat/completions?api-version=${azureApiVersion}`
      case 'ollama':
        return `${baseUrl}/api/chat`
      default:
        return `${baseUrl}/v1/chat/completions`
    }
  }

  const performHealthCheck = async () => {
    if (!baseUrl.trim()) return

    setIsHealthChecking(true)
    setHealthResult(null)

    try {
      const response = await fetch('/api/user/endpoints/health-check', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          type: endpointType,
          baseUrl: baseUrl.trim(),
          apiKey: apiKey.trim() || undefined,
          azureDeployment: endpointType === 'azure' ? azureDeployment : undefined,
          azureApiVersion: endpointType === 'azure' ? azureApiVersion : undefined
        })
      })

      const result = await response.json()

      if (response.ok) {
        setHealthResult({
          healthy: true,
          models: result.models || []
        })
      } else {
        setHealthResult({
          healthy: false,
          error: result.error || 'Health check failed'
        })
      }
    } catch (error) {
      setHealthResult({
        healthy: false,
        error: 'Network error during health check'
      })
    } finally {
      setIsHealthChecking(false)
    }
  }

  const saveEndpoint = async () => {
    if (!healthResult?.healthy) return

    try {
      const response = await fetch('/api/user/endpoints', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          name: name.trim() || `${endpointType} endpoint`,
          type: endpointType,
          baseUrl: baseUrl.trim(),
          apiKey: apiKey.trim() || undefined,
          azureDeployment: endpointType === 'azure' ? azureDeployment : undefined,
          azureApiVersion: endpointType === 'azure' ? azureApiVersion : undefined,
          models: healthResult.models || []
        })
      })

      if (response.ok) {
        onSave()
        onClose()
      } else {
        const error = await response.json()
        alert('Failed to save endpoint: ' + error.error)
      }
    } catch (error) {
      alert('Failed to save endpoint: Network error')
    }
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <h2 className="text-lg font-semibold mb-4">Add Custom Endpoint</h2>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Endpoint Type</label>
            <select
              value={endpointType}
              onChange={(e) => {
                setEndpointType(e.target.value as any)
                setHealthResult(null)
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="custom">Custom OpenAI-Compatible</option>
              <option value="azure">Azure OpenAI</option>
              <option value="ollama">Ollama (Local)</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder={`My ${endpointType} endpoint`}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Base URL</label>
            <input
              type="url"
              value={baseUrl}
              onChange={(e) => {
                setBaseUrl(e.target.value)
                setHealthResult(null)
              }}
              placeholder={
                endpointType === 'azure' 
                  ? 'https://your-resource.openai.azure.com'
                  : endpointType === 'ollama'
                  ? 'http://localhost:11434'
                  : 'https://api.example.com'
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {endpointType === 'azure' && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Deployment Name</label>
                <input
                  type="text"
                  value={azureDeployment}
                  onChange={(e) => {
                    setAzureDeployment(e.target.value)
                    setHealthResult(null)
                  }}
                  placeholder="gpt-4"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">API Version</label>
                <input
                  type="text"
                  value={azureApiVersion}
                  onChange={(e) => {
                    setAzureApiVersion(e.target.value)
                    setHealthResult(null)
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </>
          )}

          {endpointType !== 'ollama' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                API Key {endpointType === 'custom' ? '(optional)' : ''}
              </label>
              <input
                type="password"
                value={apiKey}
                onChange={(e) => {
                  setApiKey(e.target.value)
                  setHealthResult(null)
                }}
                placeholder={endpointType === 'azure' ? 'Azure API Key' : 'API Key'}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
          )}

          <div className="bg-gray-50 p-3 rounded-lg">
            <p className="text-xs text-gray-600 mb-2">Full URL:</p>
            <code className="text-xs bg-white p-2 rounded border block break-all">
              {buildUrl()}
            </code>
          </div>

          <Button
            variant="outline"
            onClick={performHealthCheck}
            disabled={!baseUrl.trim() || isHealthChecking}
            className="w-full"
          >
            {isHealthChecking ? 'Checking...' : 'Test Connection'}
          </Button>

          {healthResult && (
            <div className={`p-3 rounded-lg ${
              healthResult.healthy 
                ? 'bg-green-50 border border-green-200' 
                : 'bg-red-50 border border-red-200'
            }`}>
              <div className="flex items-center space-x-2">
                {healthResult.healthy ? (
                  <Check className="w-4 h-4 text-green-600" />
                ) : (
                  <AlertTriangle className="w-4 h-4 text-red-600" />
                )}
                <span className={`text-sm font-medium ${
                  healthResult.healthy ? 'text-green-800' : 'text-red-800'
                }`}>
                  {healthResult.healthy 
                    ? `Healthy! Found ${healthResult.models?.length || 0} models`
                    : healthResult.error
                  }
                </span>
              </div>
            </div>
          )}
        </div>

        <div className="flex space-x-3 mt-6">
          <Button
            onClick={saveEndpoint}
            disabled={!healthResult?.healthy}
            className="flex-1"
          >
            Save Endpoint
          </Button>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
        </div>
      </div>
    </div>
  )
}


