// lib/billing/usage-tracker.ts
export interface TokenUsage {
  providerId: string
  modelId: string
  inputTokens: number
  outputTokens: number
  totalCost: number
  timestamp: Date
  userId: string
  spaceId?: string
}

export interface CreditBalance {
  available: number
  used: number
  total: number
  expiresAt?: Date
  packageId?: string
}

export class UsageTracker {
  private providerRates: Map<string, { input: number; output: number }> = new Map()

  constructor() {
    this.initializeRates()
  }

  private initializeRates() {
    // Rates per 1M tokens in USD
    this.providerRates.set('gpt-3.5-turbo', { input: 0.50, output: 1.50 })
    this.providerRates.set('gpt-4', { input: 10.00, output: 30.00 })
    this.providerRates.set('gpt-4-turbo', { input: 10.00, output: 30.00 })
    this.providerRates.set('claude-3-haiku', { input: 0.25, output: 1.25 })
    this.providerRates.set('claude-3-sonnet', { input: 3.00, output: 15.00 })
    this.providerRates.set('claude-3-opus', { input: 15.00, output: 75.00 })
    this.providerRates.set('gemini-pro', { input: 0.50, output: 1.50 })
    this.providerRates.set('gemini-pro-vision', { input: 0.50, output: 1.50 })
  }

  calculateCost(modelId: string, inputTokens: number, outputTokens: number): number {
    const rates = this.providerRates.get(modelId) || { input: 1.0, output: 3.0 }

    const inputCost = (inputTokens / 1_000_000) * rates.input
    const outputCost = (outputTokens / 1_000_000) * rates.output

    return Math.round((inputCost + outputCost) * 10000) / 10000 // Round to 4 decimal places
  }

  async trackUsage(usage: TokenUsage): Promise<void> {
    // Store in database with space attribution
    await db.tokenUsage.create({
      data: {
        userId: usage.userId,
        spaceId: usage.spaceId,
        providerId: usage.providerId,
        modelId: usage.modelId,
        inputTokens: usage.inputTokens,
        outputTokens: usage.outputTokens,
        totalCost: usage.totalCost,
        timestamp: usage.timestamp
      }
    })

    // Update space balance if applicable
    if (usage.spaceId) {
      await this.deductFromSpace(usage.spaceId, usage.totalCost)
    }
  }

  private async deductFromSpace(spaceId: string, cost: number): Promise<void> {
    await db.$transaction(async (tx) => {
      const space = await tx.space.findUnique({
        where: { id: spaceId },
        include: { subscription: true }
      })

      if (!space) throw new Error('Space not found')

      // Enterprise subscriptions have unlimited usage
      if (space.subscription && space.subscription.plan === 'enterprise') {
        return
      }

      // Deduct from prepaid balance
      const currentBalance = await tx.creditBalance.findFirst({
        where: { spaceId, expiresAt: { gte: new Date() } },
        orderBy: { expiresAt: 'asc' }
      })

      if (!currentBalance || currentBalance.available < cost) {
        throw new Error('Insufficient credits')
      }

      await tx.creditBalance.update({
        where: { id: currentBalance.id },
        data: {
          available: currentBalance.available - cost,
          used: currentBalance.used + cost
        }
      })
    })
  }

  async getSpaceBalance(spaceId: string): Promise<CreditBalance> {
    const balances = await db.creditBalance.findMany({
      where: { 
        spaceId,
        expiresAt: { gte: new Date() }
      },
      orderBy: { expiresAt: 'asc' }
    })

    const total = balances.reduce((sum, b) => sum + b.total, 0)
    const available = balances.reduce((sum, b) => sum + b.available, 0)
    const used = balances.reduce((sum, b) => sum + b.used, 0)

    return { available, used, total }
  }
}

