import React, { useState, useEffect } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Calculator, DollarSign, Zap, Clock } from 'lucide-react'

interface ModelPricing {
  id: string
  name: string
  provider: string
  inputPrice: number  // per 1M tokens
  outputPrice: number // per 1M tokens
  avgSpeed: number    // tokens per second
}

export default function PricingCalculator() {
  const [inputText, setInputText] = useState('')
  const [outputLength, setOutputLength] = useState(500)
  const [selectedModel, setSelectedModel] = useState('gpt-3.5-turbo')
  const [monthlyRequests, setMonthlyRequests] = useState(1000)
  const [calculations, setCalculations] = useState<any>(null)

  const models: ModelPricing[] = [
    {
      id: 'gpt-3.5-turbo',
      name: 'GPT-3.5 Turbo',
      provider: 'OpenAI',
      inputPrice: 0.50,
      outputPrice: 1.50,
      avgSpeed: 50
    },
    {
      id: 'gpt-4',
      name: 'GPT-4',
      provider: 'OpenAI',
      inputPrice: 10.00,
      outputPrice: 30.00,
      avgSpeed: 20
    },
    {
      id: 'gpt-4-turbo',
      name: 'GPT-4 Turbo',
      provider: 'OpenAI',
      inputPrice: 10.00,
      outputPrice: 30.00,
      avgSpeed: 35
    },
    {
      id: 'claude-3-haiku',
      name: 'Claude 3 Haiku',
      provider: 'Anthropic',
      inputPrice: 0.25,
      outputPrice: 1.25,
      avgSpeed: 60
    },
    {
      id: 'claude-3-sonnet',
      name: 'Claude 3 Sonnet',
      provider: 'Anthropic',
      inputPrice: 3.00,
      outputPrice: 15.00,
      avgSpeed: 40
    },
    {
      id: 'claude-3-opus',
      name: 'Claude 3 Opus',
      provider: 'Anthropic',
      inputPrice: 15.00,
      outputPrice: 75.00,
      avgSpeed: 25
    },
    {
      id: 'gemini-pro',
      name: 'Gemini Pro',
      provider: 'Google',
      inputPrice: 0.50,
      outputPrice: 1.50,
      avgSpeed: 45
    }
  ]

  const estimateTokens = (text: string): number => {
    // Rough estimation: ~4 characters per token
    return Math.ceil(text.length / 4)
  }

  const calculateCosts = () => {
    const model = models.find(m => m.id === selectedModel)
    if (!model) return

    const inputTokens = estimateTokens(inputText)
    const outputTokens = outputLength

    // Per request costs
    const inputCost = (inputTokens / 1_000_000) * model.inputPrice
    const outputCost = (outputTokens / 1_000_000) * model.outputPrice
    const totalPerRequest = inputCost + outputCost

    // Monthly costs
    const monthlyInputCost = inputCost * monthlyRequests
    const monthlyOutputCost = outputCost * monthlyRequests
    const monthlyTotal = totalPerRequest * monthlyRequests

    // Time estimates
    const totalTokens = inputTokens + outputTokens
    const estimatedTime = totalTokens / model.avgSpeed

    // Credit pack recommendations
    const creditPacks = [
      { name: 'Starter', credits: 500, price: 9.99 },
      { name: 'Growth', credits: 2000, price: 39.99 },
      { name: 'Pro', credits: 5000, price: 99.99 },
      { name: 'Enterprise', credits: 10000, price: 249.99 }
    ]

    const recommendedPack = creditPacks.find(pack => 
      (monthlyTotal * 100) <= pack.price * 0.8
    ) || creditPacks[creditPacks.length - 1]

    setCalculations({
      model,
      inputTokens,
      outputTokens,
      inputCost,
      outputCost,
      totalPerRequest,
      monthlyInputCost,
      monthlyOutputCost,
      monthlyTotal,
      estimatedTime,
      recommendedPack,
      creditPacks
    })
  }

  useEffect(() => {
    if (inputText || outputLength || selectedModel || monthlyRequests) {
      calculateCosts()
    }
  }, [inputText, outputLength, selectedModel, monthlyRequests])

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 4
    }).format(amount)
  }

  return (
    <div className="max-w-6xl mx-auto p-6">
      <div className="text-center mb-8">
        <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-100 rounded-full mb-4">
          <Calculator className="w-8 h-8 text-blue-600" />
        </div>
        <h1 className="text-3xl font-bold text-gray-900 mb-2">AI Pricing Calculator</h1>
        <p className="text-lg text-gray-600">
          Estimate costs for your AI operations with transparent, real-time pricing
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Input Configuration */}
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Configuration</CardTitle>
              <CardDescription>Set up your usage parameters</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  AI Model
                </label>
                <select
                  value={selectedModel}
                  onChange={(e) => setSelectedModel(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {models.map((model) => (
                    <option key={model.id} value={model.id}>
                      {model.name} ({model.provider})
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Sample Input Text
                </label>
                <textarea
                  value={inputText}
                  onChange={(e) => setInputText(e.target.value)}
                  placeholder="Enter your typical input text here to estimate token usage..."
                  className="w-full h-32 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
                />
                <div className="flex justify-between text-xs text-gray-500 mt-1">
                  <span>{inputText.length} characters</span>
                  <span> {estimateTokens(inputText)} tokens</span>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Expected Output Length (tokens)
                </label>
                <input
                  type="number"
                  value={outputLength}
                  onChange={(e) => setOutputLength(parseInt(e.target.value) || 0)}
                  min="1"
                  max="4096"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <div className="text-xs text-gray-500 mt-1">
                   {Math.round(outputLength * 4)} characters
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Monthly Requests
                </label>
                <input
                  type="number"
                  value={monthlyRequests}
                  onChange={(e) => setMonthlyRequests(parseInt(e.target.value) || 0)}
                  min="1"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </CardContent>
          </Card>

          {/* Model Comparison */}
          <Card>
            <CardHeader>
              <CardTitle>Model Comparison</CardTitle>
              <CardDescription>Pricing per 1M tokens</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {models.map((model) => (
                  <div 
                    key={model.id} 
                    className={`p-3 rounded-lg border cursor-pointer transition-all ${
                      selectedModel === model.id 
                        ? 'border-blue-500 bg-blue-50' 
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => setSelectedModel(model.id)}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <div className="font-medium text-sm">{model.name}</div>
                        <div className="text-xs text-gray-500">{model.provider}</div>
                      </div>
                      <div className="text-right text-xs">
                        <div>In: {formatCurrency(model.inputPrice)}</div>
                        <div>Out: {formatCurrency(model.outputPrice)}</div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
