// lib/spaces/space-manager.ts
export interface Space {
  id: string
  name: string
  description?: string
  ownerId: string
  settings: {
    allowMemberKeys: boolean
    allowCustomEndpoints: boolean
    sharePurchasedPacks: boolean
    retentionPolicy: 'zero' | 'standard'
  }
  subscription?: {
    plan: 'starter' | 'growth' | 'pro' | 'enterprise'
    seats: number
    unlimitedTokens: boolean
  }
  createdAt: Date
  updatedAt: Date
}

export interface SpaceMember {
  id: string
  spaceId: string
  userId: string
  role: 'owner' | 'admin' | 'member'
  joinedAt: Date
  invitedBy: string
  permissions: {
    canInvite: boolean
    canManageBilling: boolean
    canViewUsage: boolean
    canManageSettings: boolean
  }
}

export class SpaceManager {
  async createSpace(ownerId: string, name: string, settings?: Partial<Space['settings']>): Promise<Space> {
    const defaultSettings = {
      allowMemberKeys: true,
      allowCustomEndpoints: true,
      sharePurchasedPacks: true,
      retentionPolicy: 'standard' as const
    }

    const space = await db.space.create({
      data: {
        name,
        ownerId,
        settings: { ...defaultSettings, ...settings }
      }
    })

    // Add owner as member
    await db.spaceMember.create({
      data: {
        spaceId: space.id,
        userId: ownerId,
        role: 'owner',
        invitedBy: ownerId,
        permissions: {
          canInvite: true,
          canManageBilling: true,
          canViewUsage: true,
          canManageSettings: true
        }
      }
    })

    return space
  }

  async inviteMember(
    spaceId: string, 
    inviterId: string, 
    email: string, 
    role: 'admin' | 'member'
  ): Promise<void> {
    // Check inviter permissions
    const inviter = await db.spaceMember.findFirst({
      where: { spaceId, userId: inviterId }
    })

    if (!inviter || (!inviter.permissions.canInvite && inviter.role !== 'owner')) {
      throw new Error('Insufficient permissions to invite members')
    }

    // Create invitation
    await db.spaceInvitation.create({
      data: {
        spaceId,
        email,
        role,
        invitedBy: inviterId,
        expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000) // 7 days
      }
    })

    // Send invitation email (implementation depends on email service)
    await this.sendInvitationEmail(spaceId, email, role, inviterId)
  }

  async transferOwnership(spaceId: string, currentOwnerId: string, newOwnerId: string): Promise<void> {
    await db.$transaction(async (tx) => {
      // Verify current owner
      const currentOwner = await tx.spaceMember.findFirst({
        where: { spaceId, userId: currentOwnerId, role: 'owner' }
      })

      if (!currentOwner) {
        throw new Error('Only the current owner can transfer ownership')
      }

      // Verify new owner is a member
      const newOwner = await tx.spaceMember.findFirst({
        where: { spaceId, userId: newOwnerId }
      })

      if (!newOwner) {
        throw new Error('New owner must be a member of the space')
      }

      // Update roles
      await tx.spaceMember.update({
        where: { id: currentOwner.id },
        data: { role: 'admin' }
      })

      await tx.spaceMember.update({
        where: { id: newOwner.id },
        data: { 
          role: 'owner',
          permissions: {
            canInvite: true,
            canManageBilling: true,
            canViewUsage: true,
            canManageSettings: true
          }
        }
      })

      // Update space owner
      await tx.space.update({
        where: { id: spaceId },
        data: { ownerId: newOwnerId }
      })
    })
  }

  async getRunHistory(spaceId: string, userId: string, days: number = 90): Promise<any[]> {
    const cutoffDate = new Date(Date.now() - days * 24 * 60 * 60 * 1000)

    return await db.bulkJob.findMany({
      where: {
        OR: [
          { userId, createdAt: { gte: cutoffDate } },
          { 
            userId: { 
              in: await db.spaceMember
                .findMany({ where: { spaceId } })
                .then(members => members.map(m => m.userId))
            },
            createdAt: { gte: cutoffDate }
          }
        ]
      },
      orderBy: { createdAt: 'desc' },
      include: {
        user: {
          select: { email: true, name: true }
        }
      }
    })
  }

  private async sendInvitationEmail(spaceId: string, email: string, role: string, inviterId: string): Promise<void> {
    // Implementation depends on your email service
    console.log(`Sending invitation to ${email} for space ${spaceId} as ${role}`)
  }
}

