// lib/auth/sso-provider.ts
import { OAuth2Client } from 'google-auth-library'

export interface SSOConfig {
  google: {
    clientId: string
    clientSecret: string
    scopes: string[]
  }
  microsoft: {
    clientId: string
    clientSecret: string
    tenantId: string
    scopes: string[]
  }
}

export class SSOProvider {
  private googleClient: OAuth2Client
  private ssoConfig: SSOConfig

  constructor() {
    this.ssoConfig = {
      google: {
        clientId: process.env.GOOGLE_CLIENT_ID!,
        clientSecret: process.env.GOOGLE_CLIENT_SECRET!,
        scopes: [
          'openid',
          'profile',
          'email',
          'https://www.googleapis.com/auth/spreadsheets', // Required for bulk tools
          'https://www.googleapis.com/auth/drive.readonly' // For file access
        ]
      },
      microsoft: {
        clientId: process.env.MICROSOFT_CLIENT_ID!,
        clientSecret: process.env.MICROSOFT_CLIENT_SECRET!,
        tenantId: process.env.MICROSOFT_TENANT_ID!,
        scopes: [
          'openid',
          'profile',
          'email',
          'https://graph.microsoft.com/Files.ReadWrite.All' // For Excel integration
        ]
      }
    }

    this.googleClient = new OAuth2Client(
      this.ssoConfig.google.clientId,
      this.ssoConfig.google.clientSecret,
      `${process.env.APP_URL}/auth/google/callback`
    )
  }

  getGoogleAuthUrl(): string {
    return this.googleClient.generateAuthUrl({
      access_type: 'offline',
      scope: this.ssoConfig.google.scopes,
      prompt: 'consent'
    })
  }

  async verifyGoogleToken(token: string): Promise<{
    email: string
    name: string
    picture?: string
  }> {
    const ticket = await this.googleClient.verifyIdToken({
      idToken: token,
      audience: this.ssoConfig.google.clientId
    })

    const payload = ticket.getPayload()
    if (!payload) throw new Error('Invalid token')

    return {
      email: payload.email!,
      name: payload.name!,
      picture: payload.picture
    }
  }

  getMicrosoftAuthUrl(): string {
    const params = new URLSearchParams({
      client_id: this.ssoConfig.microsoft.clientId,
      response_type: 'code',
      redirect_uri: `${process.env.APP_URL}/auth/microsoft/callback`,
      scope: this.ssoConfig.microsoft.scopes.join(' '),
      response_mode: 'query'
    })

    return `https://login.microsoftonline.com/${this.ssoConfig.microsoft.tenantId}/oauth2/v2.0/authorize?${params}`
  }
}

