Google Sheets AI Add-on - Core Functions


// apps-script/Code.gs - Main Google Apps Script file for AI Sheets add-on
/**
 * AI Sheets Google Workspace Add-on
 * Provides AI functions directly in Google Sheets
 */

// Configuration
const API_BASE_URL = 'https://api.aisheets.com'
const SIDEBAR_TITLE = 'AI Sheets'
const VERSION = '1.0.0'

/**
 * Called when add-on is installed or document is opened
 */
function onInstall(e) {
  onOpen(e)
}

/**
 * Called when document is opened - creates the add-on menu
 */
function onOpen(e) {
  const ui = SpreadsheetApp.getUi()
  ui.createAddonMenu()
    .addItem('Open AI Functions', 'showSidebar')
    .addItem('Batch Process Range', 'showBatchProcessor')
    .addItem('Function Helper', 'showFunctionHelper')
    .addSeparator()
    .addItem('Settings', 'showSettings')
    .addItem('Usage & Credits', 'showUsage')
    .addItem('About', 'showAbout')
    .addToUi()
}

// =============================================================================
// CORE AI FUNCTIONS - Available as spreadsheet formulas
// =============================================================================

/**
 * Generate text using AI
 * @param {string} prompt The text prompt
 * @param {string} model Optional model to use (default: gpt-3.5-turbo)
 * @param {number} temperature Optional temperature 0-1 (default: 0.7)
 * @return {string} Generated text
 * @customfunction
 */
function GPT(prompt, model = 'gpt-3.5-turbo', temperature = 0.7) {
  if (!prompt) return '#ERROR: Prompt is required'

  try {
    const result = callAIFunction('gpt', {
      input: prompt.toString(),
      options: { model, temperature: Number(temperature) }
    })

    return result.text || '#ERROR: No response'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Translate text to another language
 * @param {string} text Text to translate
 * @param {string} targetLanguage Target language (e.g., "Spanish", "French")
 * @return {string} Translated text
 * @customfunction
 */
function GPT_TRANSLATE(text, targetLanguage = 'Spanish') {
  if (!text) return '#ERROR: Text is required'

  try {
    const result = callAIFunction('gpt_translate', {
      input: text.toString(),
      options: { targetLanguage: targetLanguage.toString() }
    })

    return result.text || '#ERROR: Translation failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Classify text into categories
 * @param {string} text Text to classify
 * @param {string} categories Comma-separated categories
 * @return {string} Classification result
 * @customfunction
 */
function GPT_CLASSIFY(text, categories = 'Positive,Neutral,Negative') {
  if (!text) return '#ERROR: Text is required'

  try {
    const categoryList = categories.toString().split(',').map(c => c.trim())
    const result = callAIFunction('gpt_classify', {
      input: text.toString(),
      options: { categories: categoryList }
    })

    return result.text || '#ERROR: Classification failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Extract specific information from text
 * @param {string} text Text to analyze
 * @param {string} fields Comma-separated fields to extract
 * @return {string} Extracted information as JSON or text
 * @customfunction
 */
function GPT_EXTRACT(text, fields = 'key information') {
  if (!text) return '#ERROR: Text is required'

  try {
    const fieldList = fields.toString().split(',').map(f => f.trim())
    const result = callAIFunction('gpt_extract', {
      input: text.toString(),
      options: { fields: fieldList, format: 'text' }
    })

    return result.text || '#ERROR: Extraction failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Summarize long text
 * @param {string} text Text to summarize
 * @param {number} maxWords Maximum summary length in words (default: 100)
 * @return {string} Summary
 * @customfunction
 */
function GPT_SUMMARIZE(text, maxWords = 100) {
  if (!text) return '#ERROR: Text is required'

  try {
    const result = callAIFunction('gpt_summarize', {
      input: text.toString(),
      options: { maxWords: Number(maxWords) }
    })

    return result.text || '#ERROR: Summarization failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Generate a list of items
 * @param {string} prompt What to generate a list about
 * @param {number} count Number of items (default: 5)
 * @return {string} Generated list
 * @customfunction
 */
function GPT_LIST(prompt, count = 5) {
  if (!prompt) return '#ERROR: Prompt is required'

  try {
    const result = callAIFunction('gpt_list', {
      input: prompt.toString(),
      options: { count: Number(count), style: 'numbered' }
    })

    return result.text || '#ERROR: List generation failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Edit/improve existing text
 * @param {string} text Original text to edit
 * @param {string} instruction How to edit the text
 * @return {string} Edited text
 * @customfunction
 */
function GPT_EDIT(text, instruction = 'improve this text') {
  if (!text) return '#ERROR: Text is required'

  try {
    const result = callAIFunction('gpt_edit', {
      input: text.toString(),
      options: { instruction: instruction.toString() }
    })

    return result.text || '#ERROR: Edit failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Format text according to specifications
 * @param {string} text Text to format
 * @param {string} format Desired format (e.g., "title case", "remove extra spaces")
 * @return {string} Formatted text
 * @customfunction
 */
function GPT_FORMAT(text, format = 'clean and format') {
  if (!text) return '#ERROR: Text is required'

  try {
    const result = callAIFunction('gpt_format', {
      input: text.toString(),
      options: { format: format.toString() }
    })

    return result.text || '#ERROR: Format failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Web search and summarize results
 * @param {string} query Search query
 * @param {number} maxResults Maximum results to consider (default: 5)
 * @return {string} Search summary
 * @customfunction
 */
function WEB_SEARCH(query, maxResults = 5) {
  if (!query) return '#ERROR: Query is required'

  try {
    const result = callAIFunction('web_search', {
      input: query.toString(),
      options: { maxResults: Number(maxResults) }
    })

    return result.text || '#ERROR: Search failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

/**
 * Check sentiment of text
 * @param {string} text Text to analyze
 * @return {string} Sentiment (Positive/Negative/Neutral with score)
 * @customfunction
 */
function GPT_SENTIMENT(text) {
  if (!text) return '#ERROR: Text is required'

  try {
    const result = callAIFunction('gpt_classify', {
      input: text.toString(),
      options: { 
        categories: ['Very Positive', 'Positive', 'Neutral', 'Negative', 'Very Negative'],
        includeScore: true
      }
    })

    return result.text || '#ERROR: Sentiment analysis failed'
  } catch (error) {
    return `#ERROR: ${error.message}`
  }
}

// =============================================================================
// CORE API COMMUNICATION
// =============================================================================

/**
 * Core function to call AI Sheets API
 */
function callAIFunction(functionName, data) {
  const userToken = getUserToken()
  const apiKey = getUserApiKey()
  const spaceId = getCurrentSpaceId()

  if (!userToken && !apiKey) {
    throw new Error('Please authenticate in Settings (Add-ons > AI Sheets > Settings)')
  }

  const headers = {
    'Content-Type': 'application/json',
    'User-Agent': `AI-Sheets-AddOn/${VERSION}`
  }

  if (userToken) {
    headers['Authorization'] = `Bearer ${userToken}`
  } else if (apiKey) {
    headers['X-API-Key'] = apiKey
  }

  // Add space context if available
  if (spaceId) {
    headers['X-Space-ID'] = spaceId
  }

  const payload = {
    function: functionName,
    ...data,
    metadata: {
      source: 'google_apps_script',
      version: VERSION,
      userEmail: Session.getActiveUser().getEmail(),
      documentId: SpreadsheetApp.getActiveSpreadsheet().getId(),
      documentName: SpreadsheetApp.getActiveSpreadsheet().getName(),
      timestamp: new Date().toISOString()
    }
  }

  const options = {
    method: 'POST',
    headers,
    payload: JSON.stringify(payload),
    muteHttpExceptions: true
  }

  try {
    const response = UrlFetchApp.fetch(`${API_BASE_URL}/api/ai/${functionName}`, options)
    const responseCode = response.getResponseCode()
    const responseData = JSON.parse(response.getContentText())

    if (responseCode === 401) {
      throw new Error('Authentication failed. Please check your credentials in Settings.')
    } else if (responseCode === 402) {
      throw new Error('Insufficient credits. Please upgrade your plan.')
    } else if (responseCode === 429) {
      throw new Error('Rate limit exceeded. Please try again in a moment.')
    } else if (responseCode >= 400) {
      throw new Error(responseData.error || `API error (${responseCode})`)
    }

    // Track usage for display
    updateUsageTracking(functionName, responseData.creditsUsed || 1)

    return responseData

  } catch (error) {
    if (error.message.includes('Exception: Request failed')) {
      throw new Error('Network error. Please check your internet connection.')
    }
    throw error
  }
}

// =============================================================================
// BATCH PROCESSING
// =============================================================================

/**
 * Process selected range with specified AI function
 */
function processBatchFromSidebar(functionName, options = {}) {
  const sheet = SpreadsheetApp.getActiveSheet()
  const range = sheet.getActiveRange()

  if (!range) {
    SpreadsheetApp.getUi().alert('Please select a range of cells first.')
    return
  }

  processBatchAI(range, functionName, options)
}

/**
 * Batch process multiple cells with AI
 * @param {Range} range Range of cells to process
 * @param {string} functionName AI function to use
 * @param {Object} options Function options
 */
function processBatchAI(range, functionName, options = {}) {
  const sheet = SpreadsheetApp.getActiveSheet()
  const values = range.getValues()
  const results = []
  const ui = SpreadsheetApp.getUi()

  // Validate range size
  const cellCount = values.length * values[0].length
  if (cellCount > 1000) {
    const proceed = ui.alert(
      'Large Range Warning',
      `You're about to process ${cellCount} cells. This may take several minutes and consume many credits. Continue?`,
      ui.ButtonSet.YES_NO
    )
    if (proceed !== ui.Button.YES) return
  }

  // Show progress dialog
  const progressHtml = `
    <div style="padding: 20px; text-align: center;">
      <h3>Processing ${cellCount} cells...</h3>
      <div id="progress">Starting...</div>
      <div style="margin-top: 20px;">
        <button onclick="google.script.host.close()">Close</button>
      </div>
    </div>
  `

  const progressDialog = HtmlService.createHtmlOutput(progressHtml)
    .setWidth(350)
    .setHeight(200)
  ui.showModalDialog(progressDialog, 'Batch Processing')

  try {
    let processedCount = 0
    let totalCredits = 0

    for (let i = 0; i < values.length; i++) {
      const row = []

      for (let j = 0; j < values[i].length; j++) {
        const cellValue = values[i][j]

        if (cellValue && cellValue.toString().trim()) {
          try {
            const result = callAIFunction(functionName, {
              input: cellValue.toString(),
              options
            })

            row.push(result.text || '')
            totalCredits += result.creditsUsed || 1

          } catch (error) {
            row.push(`#ERROR: ${error.message}`)
          }
        } else {
          row.push('') // Empty cell
        }

        processedCount++

        // Update progress every 10 cells
        if (processedCount % 10 === 0) {
          console.log(`Progress: ${processedCount}/${cellCount} cells processed`)
        }
      }

      results.push(row)
    }

    // Write results to adjacent columns or user-specified location
    const outputRange = getOutputRange(range, results)
    outputRange.setValues(results)

    // Show completion message
    ui.alert(
      'Batch Processing Complete!',
      `‚úÖ Processed ${cellCount} cells\\nüí∞ Credits used: ${totalCredits}\\nüìç Results written to ${outputRange.getA1Notation()}`,
      ui.ButtonSet.OK
    )

  } catch (error) {
    ui.alert('Batch Processing Failed', `Error: ${error.message}`, ui.ButtonSet.OK)
  }
}

/**
 * Determine where to place batch processing results
 */
function getOutputRange(inputRange, results) {
  const sheet = SpreadsheetApp.getActiveSheet()
  const startRow = inputRange.getRow()
  const startCol = inputRange.getLastColumn() + 1

  // Check if there's space for output
  const requiredCols = results[0].length
  const maxCols = sheet.getMaxColumns()

  if (startCol + requiredCols - 1 > maxCols) {
    // Not enough columns, ask user for location
    const ui = SpreadsheetApp.getUi()
    const response = ui.prompt(
      'Output Location',
      'Enter cell reference for results (e.g., E1):',
      ui.ButtonSet.OK_CANCEL
    )

    if (response.getSelectedButton() === ui.Button.OK) {
      const targetCell = response.getResponseText().trim()
      try {
        return sheet.getRange(targetCell).offset(0, 0, results.length, results[0].length)
      } catch (error) {
        throw new Error(`Invalid cell reference: ${targetCell}`)
      }
    } else {
      throw new Error('Batch processing cancelled')
    }
  }

  return sheet.getRange(startRow, startCol, results.length, results[0].length)
}

// =============================================================================
// USER INTERFACE FUNCTIONS
// =============================================================================

/**
 * Show the main AI functions sidebar
 */
function showSidebar() {
  const html = HtmlService.createTemplateFromFile('sidebar')
  html.userEmail = Session.getActiveUser().getEmail()
  html.apiKey = getUserApiKey() ? '‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢' : ''
  html.isAuthenticated = isUserAuthenticated()
  html.currentSpace = getCurrentSpaceName()

  const htmlOutput = html.evaluate()
    .setWidth(380)
    .setTitle(SIDEBAR_TITLE)

  SpreadsheetApp.getUi().showSidebar(htmlOutput)
}

/**
 * Show batch processing interface
 */
function showBatchProcessor() {
  const html = HtmlService.createTemplateFromFile('batch-processor')
  const htmlOutput = html.evaluate()
    .setWidth(450)
    .setHeight(500)
    .setTitle('Batch AI Processing')

  SpreadsheetApp.getUi().showModalDialog(htmlOutput, 'Batch Processing')
}

/**
 * Show function helper with examples
 */
function showFunctionHelper() {
  const html = HtmlService.createTemplateFromFile('function-helper')
  const htmlOutput = html.evaluate()
    .setWidth(600)
    .setHeight(500)
    .setTitle('AI Function Helper')

  SpreadsheetApp.getUi().showModalDialog(htmlOutput, 'Function Helper')
}

/**
 * Show settings dialog
 */
function showSettings() {
  const html = HtmlService.createTemplateFromFile('settings')
  html.apiKey = getUserApiKey() || ''
  html.userEmail = Session.getActiveUser().getEmail()
  html.currentSpace = getCurrentSpaceName()
  html.isAuthenticated = isUserAuthenticated()

  const htmlOutput = html.evaluate()
    .setWidth(500)
    .setHeight(400)
    .setTitle('AI Sheets Settings')

  SpreadsheetApp.getUi().showModalDialog(htmlOutput, 'Settings')
}

/**
 * Show usage and credits information
 */
function showUsage() {
  try {
    const usage = getUserUsage()
    const html = HtmlService.createTemplateFromFile('usage')
    html.usage = usage

    const htmlOutput = html.evaluate()
      .setWidth(500)
      .setHeight(400)
      .setTitle('Usage & Credits')

    SpreadsheetApp.getUi().showModalDialog(htmlOutput, 'Usage & Credits')
  } catch (error) {
    SpreadsheetApp.getUi().alert(`Failed to load usage data: ${error.message}`)
  }
}

/**
 * Show about dialog
 */
function showAbout() {
  const ui = SpreadsheetApp.getUi()
  ui.alert(
    'AI Sheets Add-on',
    `ü§ñ AI Sheets brings powerful AI functions to Google Workspace.\\n\\n` +
    `Version: ${VERSION}\\n` +
    `Website: https://aisheets.com\\n` +
    `Support: support@aisheets.com\\n\\n` +
    `¬© 2025 AI Sheets. All rights reserved.`,
    ui.ButtonSet.OK
  )
}

// =============================================================================
// UTILITY FUNCTIONS FOR UI
// =============================================================================

/**
 * Insert formula at active cell
 */
function insertFormulaInActiveCell(formula) {
  try {
    const sheet = SpreadsheetApp.getActiveSheet()
    const range = sheet.getActiveRange()

    if (range.getNumRows() === 1 && range.getNumColumns() === 1) {
      range.setFormula(formula)
      return { success: true }
    } else {
      throw new Error('Please select a single cell')
    }
  } catch (error) {
    return { success: false, error: error.message }
  }
}

/**
 * Get current sheet statistics
 */
function getSheetStats() {
  const sheet = SpreadsheetApp.getActiveSheet()
  const range = sheet.getDataRange()

  return {
    sheetName: sheet.getName(),
    rows: range.getNumRows(),
    columns: range.getNumColumns(),
    cells: range.getNumRows() * range.getNumColumns()
  }
}

/**
 * Include HTML files in the script
 */
function include(filename) {
  return HtmlService.createHtmlOutputFromFile(filename).getContent()
}

// =============================================================================
// AUTHENTICATION & SETTINGS MANAGEMENT
// =============================================================================

function getUserToken() {
  return PropertiesService.getUserProperties().getProperty('AI_SHEETS_TOKEN')
}

function setUserToken(token) {
  PropertiesService.getUserProperties().setProperty('AI_SHEETS_TOKEN', token)
}

function getUserApiKey() {
  return PropertiesService.getUserProperties().getProperty('AI_SHEETS_API_KEY')
}

function setUserApiKey(apiKey) {
  if (apiKey && apiKey.trim()) {
    PropertiesService.getUserProperties().setProperty('AI_SHEETS_API_KEY', apiKey.trim())
    return { success: true }
  } else {
    PropertiesService.getUserProperties().deleteProperty('AI_SHEETS_API_KEY')
    return { success: true, message: 'API key cleared' }
  }
}

function getCurrentSpaceId() {
  return PropertiesService.getUserProperties().getProperty('AI_SHEETS_SPACE_ID')
}

function setCurrentSpaceId(spaceId) {
  if (spaceId) {
    PropertiesService.getUserProperties().setProperty('AI_SHEETS_SPACE_ID', spaceId)
  } else {
    PropertiesService.getUserProperties().deleteProperty('AI_SHEETS_SPACE_ID')
  }
}

function getCurrentSpaceName() {
  return PropertiesService.getUserProperties().getProperty('AI_SHEETS_SPACE_NAME') || 'Personal'
}

function setCurrentSpaceName(spaceName) {
  if (spaceName) {
    PropertiesService.getUserProperties().setProperty('AI_SHEETS_SPACE_NAME', spaceName)
  } else {
    PropertiesService.getUserProperties().deleteProperty('AI_SHEETS_SPACE_NAME')
  }
}

function isUserAuthenticated() {
  return !!(getUserToken() || getUserApiKey())
}

function clearUserAuth() {
  const properties = PropertiesService.getUserProperties()
  properties.deleteProperty('AI_SHEETS_TOKEN')
  properties.deleteProperty('AI_SHEETS_API_KEY')
  properties.deleteProperty('AI_SHEETS_SPACE_ID')
  properties.deleteProperty('AI_SHEETS_SPACE_NAME')
  return { success: true }
}

/**
 * Test API connection
 */
function testConnection() {
  try {
    const result = callAIFunction('test', { input: 'connection test' })
    return { success: true, message: 'Connection successful!' }
  } catch (error) {
    return { success: false, error: error.message }
  }
}

/**
 * Get user usage statistics
 */
function getUserUsage() {
  try {
    const headers = {
      'Content-Type': 'application/json'
    }

    const userToken = getUserToken()
    const apiKey = getUserApiKey()

    if (userToken) {
      headers['Authorization'] = `Bearer ${userToken}`
    } else if (apiKey) {
      headers['X-API-Key'] = apiKey
    } else {
      throw new Error('No authentication credentials found')
    }

    const options = {
      method: 'GET',
      headers,
      muteHttpExceptions: true
    }

    const response = UrlFetchApp.fetch(`${API_BASE_URL}/api/user/usage`, options)
    const responseData = JSON.parse(response.getContentText())

    if (response.getResponseCode() !== 200) {
      throw new Error(responseData.error || 'Failed to fetch usage data')
    }

    return responseData

  } catch (error) {
    throw new Error(`Usage fetch failed: ${error.message}`)
  }
}

/**
 * Update local usage tracking
 */
function updateUsageTracking(functionName, credits) {
  try {
    const properties = PropertiesService.getUserProperties()
    const today = new Date().toDateString()
    const usageKey = `USAGE_${today}`

    let todayUsage = {}
    const existing = properties.getProperty(usageKey)
    if (existing) {
      todayUsage = JSON.parse(existing)
    }

    if (!todayUsage[functionName]) {
      todayUsage[functionName] = { calls: 0, credits: 0 }
    }

    todayUsage[functionName].calls += 1
    todayUsage[functionName].credits += credits

    properties.setProperty(usageKey, JSON.stringify(todayUsage))
  } catch (error) {
    console.error('Failed to update usage tracking:', error)
  }
}

// =============================================================================
// OAUTH AUTHENTICATION FLOW
// =============================================================================

/**
 * Start OAuth authentication process
 */
function startOAuthFlow() {
  const authUrl = `${API_BASE_URL}/api/auth/google?source=apps_script&state=${Utilities.getUuid()}`

  const ui = SpreadsheetApp.getUi()
  const result = ui.alert(
    'Authentication Required',
    `To use AI functions, you need to authenticate with AI Sheets.\\n\\nClick OK to open the authentication page in your browser.`,
    ui.ButtonSet.OK_CANCEL
  )

  if (result === ui.Button.OK) {
    return {
      success: true,
      authUrl,
      instructions: `Please visit this URL to authenticate:\\n\\n${authUrl}\\n\\nAfter completing authentication, return here and click "Complete Authentication".`
    }
  }

  return { success: false, message: 'Authentication cancelled' }
}

/**
 * Complete OAuth authentication
 */
function completeOAuthFlow(token) {
  if (!token) {
    return { success: false, error: 'No token provided' }
  }

  try {
    setUserToken(token)

    // Test the token
    const testResult = testConnection()
    if (testResult.success) {
      return { success: true, message: 'Authentication completed successfully!' }
    } else {
      clearUserAuth()
      return { success: false, error: 'Invalid token' }
    }
  } catch (error) {
    return { success: false, error: error.message }
  }
}


