  <script>
    // Function insertion with proper formula generation
    function insertFunction(funcName) {
      let formula = '';

      switch(funcName) {
        case 'GPT':
          formula = '=GPT("Your prompt here")';
          break;
        case 'GPT_TRANSLATE':
          formula = '=GPT_TRANSLATE(A1, "Spanish")';
          break;
        case 'GPT_CLASSIFY':
          formula = '=GPT_CLASSIFY(A1, "Positive,Neutral,Negative")';
          break;
        case 'GPT_EXTRACT':
          formula = '=GPT_EXTRACT(A1, "name,email,phone")';
          break;
        case 'GPT_SUMMARIZE':
          formula = '=GPT_SUMMARIZE(A1, 100)';
          break;
        case 'GPT_LIST':
          formula = '=GPT_LIST("Marketing ideas for small business", 5)';
          break;
        case 'GPT_EDIT':
          formula = '=GPT_EDIT(A1, "make more professional")';
          break;
        case 'GPT_FORMAT':
          formula = '=GPT_FORMAT(A1, "title case")';
          break;
        case 'WEB_SEARCH':
          formula = '=WEB_SEARCH("latest AI trends 2025", 3)';
          break;
        case 'GPT_SENTIMENT':
          formula = '=GPT_SENTIMENT(A1)';
          break;
      }

      google.script.run
        .withSuccessHandler((result) => {
          if (!result.success) {
            alert('Error: ' + result.error);
          }
        })
        .withFailureHandler((error) => {
          alert('Failed to insert formula: ' + error.message);
        })
        .insertFormulaInActiveCell(formula);
    }

    // Batch processing with dynamic options
    function processBatch() {
      const functionName = document.getElementById('batchFunction').value;
      const processBatchBtn = document.getElementById('processBatchBtn');

      // Build options based on selected function
      const options = {};

      switch(functionName) {
        case 'gpt_translate':
          const targetLanguage = document.getElementById('targetLanguage').value;
          if (targetLanguage) options.targetLanguage = targetLanguage;
          break;
        case 'gpt_classify':
          const categories = document.getElementById('categories').value;
          if (categories) options.categories = categories.split(',').map(c => c.trim());
          break;
        case 'gpt_extract':
          const fields = document.getElementById('extractFields').value;
          if (fields) options.fields = fields.split(',').map(f => f.trim());
          break;
        case 'gpt_edit':
          const instruction = document.getElementById('editInstruction').value;
          if (instruction) options.instruction = instruction;
          break;
        case 'gpt_format':
          const format = document.getElementById('formatStyle').value;
          if (format) options.format = format;
          break;
        case 'gpt_summarize':
          const maxWords = document.getElementById('maxWords').value;
          if (maxWords) options.maxWords = parseInt(maxWords);
          break;
      }

      // Show loading state
      processBatchBtn.textContent = 'Processing...';
      processBatchBtn.disabled = true;
      processBatchBtn.classList.add('loading');

      google.script.run
        .withSuccessHandler(() => {
          processBatchBtn.textContent = 'Process Selected Range';
          processBatchBtn.disabled = false;
          processBatchBtn.classList.remove('loading');
        })
        .withFailureHandler((error) => {
          alert('Batch processing failed: ' + error.message);
          processBatchBtn.textContent = 'Process Selected Range';
          processBatchBtn.disabled = false;
          processBatchBtn.classList.remove('loading');
        })
        .processBatchFromSidebar(functionName, options);
    }

    // Dynamic options display
    document.getElementById('batchFunction').addEventListener('change', function() {
      const value = this.value;

      // Hide all option groups
      document.querySelectorAll('#batchOptions .form-group').forEach(group => {
        group.style.display = 'none';
      });

      // Show relevant options
      switch(value) {
        case 'gpt_translate':
          document.getElementById('targetLanguageGroup').style.display = 'block';
          break;
        case 'gpt_classify':
          document.getElementById('categoriesGroup').style.display = 'block';
          break;
        case 'gpt_extract':
          document.getElementById('fieldsGroup').style.display = 'block';
          break;
        case 'gpt_edit':
          document.getElementById('instructionGroup').style.display = 'block';
          break;
        case 'gpt_format':
          document.getElementById('formatGroup').style.display = 'block';
          break;
        case 'gpt_summarize':
          document.getElementById('maxWordsGroup').style.display = 'block';
          break;
      }
    });

    // Helper functions
    function openSettings() {
      google.script.run.showSettings();
    }

    function showFunctionHelper() {
      google.script.run.showFunctionHelper();
    }

    function showUsage() {
      google.script.run.showUsage();
    }
  </script>
</body>
</html>


