  <script>
    let authFlowStarted = false;

    // OAuth Flow Functions
    function startOAuth() {
      const oauthBtn = document.getElementById('oauthBtn');
      const completeBtn = document.getElementById('completeBtn');

      oauthBtn.textContent = 'Starting...';
      oauthBtn.disabled = true;

      google.script.run
        .withSuccessHandler((result) => {
          if (result.success) {
            authFlowStarted = true;

            // Open authentication URL in new window
            window.open(result.authUrl, '_blank', 'width=600,height=700,scrollbars=yes');

            // Update UI to show next steps
            oauthBtn.textContent = 'Authentication Window Opened';
            oauthBtn.disabled = true;
            completeBtn.disabled = false;
            completeBtn.textContent = 'Complete Setup';

            alert(result.instructions);
          } else {
            oauthBtn.textContent = 'Start OAuth Flow';
            oauthBtn.disabled = false;
            alert('Failed to start OAuth flow: ' + result.message);
          }
        })
        .withFailureHandler((error) => {
          oauthBtn.textContent = 'Start OAuth Flow';
          oauthBtn.disabled = false;
          alert('Error starting OAuth: ' + error.message);
        })
        .startOAuthFlow();
    }

    function completeOAuth() {
      if (!authFlowStarted) {
        alert('Please start the OAuth flow first.');
        return;
      }

      const token = prompt('Enter the token from the authentication page:');
      if (!token) {
        return;
      }

      const completeBtn = document.getElementById('completeBtn');
      completeBtn.textContent = 'Verifying...';
      completeBtn.disabled = true;

      google.script.run
        .withSuccessHandler((result) => {
          if (result.success) {
            alert(result.message);
            location.reload(); // Refresh to show updated auth status
          } else {
            alert('Authentication failed: ' + result.error);
            completeBtn.textContent = 'Complete Setup';
            completeBtn.disabled = false;
          }
        })
        .withFailureHandler((error) => {
          alert('Authentication error: ' + error.message);
          completeBtn.textContent = 'Complete Setup';
          completeBtn.disabled = false;
        })
        .completeOAuthFlow(token);
    }

    // Settings Management
    function saveSettings() {
      const apiKey = document.getElementById('apiKey').value;
      const defaultModel = document.getElementById('defaultModel').value;
      const saveBtn = document.getElementById('saveBtn');

      saveBtn.textContent = 'Saving...';
      saveBtn.disabled = true;
      saveBtn.classList.add('loading');

      google.script.run
        .withSuccessHandler((result) => {
          if (result.success) {
            alert('Settings saved successfully!');
            if (result.message) {
              alert(result.message);
            }
          } else {
            alert('Failed to save settings: ' + result.error);
          }
          saveBtn.textContent = 'Save Settings';
          saveBtn.disabled = false;
          saveBtn.classList.remove('loading');
        })
        .withFailureHandler((error) => {
          alert('Failed to save settings: ' + error.message);
          saveBtn.textContent = 'Save Settings';
          saveBtn.disabled = false;
          saveBtn.classList.remove('loading');
        })
        .setUserApiKey(apiKey);
    }

    function testConnection() {
      const testBtn = document.getElementById('testBtn');
      testBtn.textContent = 'Testing...';
      testBtn.disabled = true;
      testBtn.classList.add('loading');

      google.script.run
        .withSuccessHandler((result) => {
          if (result.success) {
            alert('‚úÖ Connection successful!\n\n' + result.message);
          } else {
            alert('‚ùå Connection failed:\n\n' + result.error);
          }
          testBtn.textContent = 'Test Connection';
          testBtn.disabled = false;
          testBtn.classList.remove('loading');
        })
        .withFailureHandler((error) => {
          alert('‚ùå Test failed:\n\n' + error.message);
          testBtn.textContent = 'Test Connection';
          testBtn.disabled = false;
          testBtn.classList.remove('loading');
        })
        .testConnection();
    }

    function clearAuth() {
      if (confirm('This will clear all authentication data. You will need to re-authenticate to use AI functions. Continue?')) {
        const clearBtn = document.getElementById('clearBtn');
        clearBtn.textContent = 'Clearing...';
        clearBtn.disabled = true;

        google.script.run
          .withSuccessHandler((result) => {
            if (result.success) {
              alert('Authentication cleared successfully.');
              location.reload(); // Refresh to show updated auth status
            } else {
              alert('Failed to clear authentication.');
              clearBtn.textContent = 'Clear Auth';
              clearBtn.disabled = false;
            }
          })
          .withFailureHandler((error) => {
            alert('Error clearing auth: ' + error.message);
            clearBtn.textContent = 'Clear Auth';
            clearBtn.disabled = false;
          })
          .clearUserAuth();
      }
    }

    // Show/hide API key
    function toggleApiKeyVisibility() {
      const apiKeyInput = document.getElementById('apiKey');
      const toggleBtn = document.getElementById('toggleApiKey');

      if (apiKeyInput.type === 'password') {
        apiKeyInput.type = 'text';
        toggleBtn.textContent = 'Hide';
      } else {
        apiKeyInput.type = 'password';
        toggleBtn.textContent = 'Show';
      }
    }

    // Initialize page
    document.addEventListener('DOMContentLoaded', function() {
      // Disable complete button initially
      document.getElementById('completeBtn').disabled = true;
    });
  </script>
</body>
