      google.script.run
        .withSuccessHandler(() => {
          refreshBtn.classList.remove('loading');
          // Reload the dialog with fresh data
          google.script.run.showUsage();
        })
        .withFailureHandler((error) => {
          refreshBtn.classList.remove('loading');
          alert('Failed to refresh usage data: ' + error.message);
        })
        .getUserUsage();
    }

    // Auto-refresh every 30 seconds if dialog is open
    setInterval(() => {
      const refreshBtn = document.getElementById('refreshBtn');
      if (!refreshBtn.classList.contains('loading')) {
        refreshUsage();
      }
    }, 30000);
