Chrome Extension Background Script


// background.js - Chrome Extension Service Worker

const API_BASE_URL = 'https://api.aisheets.com';

// Extension installation and updates
chrome.runtime.onInstalled.addListener((details) => {
  if (details.reason === 'install') {
    console.log('AI Sheets extension installed');

    // Set default settings
    chrome.storage.sync.set({
      'ai_sheets_settings': {
        apiKey: '',
        defaultModel: 'gpt-3.5-turbo',
        maxTokens: 1000,
        temperature: 0.7,
        enableNotifications: true
      }
    });

    // Open welcome page
    chrome.tabs.create({
      url: 'https://aisheets.com/welcome-extension'
    });
  }
});

// Handle messages from content scripts and popup
chrome.runtime.onMessage.addListener((request, sender, sendResponse) => {
  switch (request.action) {
    case 'callAI':
      handleAIRequest(request.data, sendResponse);
      return true; // Keep message channel open for async response

    case 'getSettings':
      getExtensionSettings(sendResponse);
      return true;

    case 'saveSettings':
      saveExtensionSettings(request.settings, sendResponse);
      return true;

    case 'testConnection':
      testAPIConnection(sendResponse);
      return true;

    case 'authenticate':
      handleOAuthAuthentication(sendResponse);
      return true;

    default:
      sendResponse({ error: 'Unknown action' });
  }
});

// AI API request handler
async function handleAIRequest(requestData, sendResponse) {
  try {
    const settings = await getStoredSettings();

    if (!settings.apiKey && !settings.authToken) {
      sendResponse({
        success: false,
        error: 'No API key or authentication token found. Please configure in extension settings.'
      });
      return;
    }

    const headers = {
      'Content-Type': 'application/json',
      'User-Agent': 'AI-Sheets-Extension/1.0.0'
    };

    if (settings.authToken) {
      headers['Authorization'] = `Bearer ${settings.authToken}`;
    } else if (settings.apiKey) {
      headers['X-API-Key'] = settings.apiKey;
    }

    const payload = {
      function: requestData.functionName,
      input: requestData.input,
      options: {
        model: requestData.model || settings.defaultModel,
        temperature: requestData.temperature || settings.temperature,
        maxTokens: requestData.maxTokens || settings.maxTokens,
        ...requestData.options
      },
      metadata: {
        source: 'chrome_extension',
        version: '1.0.0',
        url: requestData.url,
        timestamp: new Date().toISOString()
      }
    };

    const response = await fetch(`${API_BASE_URL}/api/ai/${requestData.functionName}`, {
      method: 'POST',
      headers,
      body: JSON.stringify(payload)
    });

    const responseData = await response.json();

    if (!response.ok) {
      throw new Error(responseData.error || `API error: ${response.status}`);
    }

    // Track usage
    updateUsageStats(requestData.functionName, responseData.creditsUsed || 1);

    sendResponse({
      success: true,
      data: responseData
    });

  } catch (error) {
    console.error('AI request failed:', error);
    sendResponse({
      success: false,
      error: error.message
    });
  }
}

// Settings management
async function getStoredSettings() {
  const result = await chrome.storage.sync.get('ai_sheets_settings');
  return result.ai_sheets_settings || {};
}

async function getExtensionSettings(sendResponse) {
  try {
    const settings = await getStoredSettings();
    const usage = await getUsageStats();

    sendResponse({
      success: true,
      settings,
      usage
    });
  } catch (error) {
    sendResponse({
      success: false,
      error: error.message
    });
  }
}

async function saveExtensionSettings(newSettings, sendResponse) {
  try {
    const currentSettings = await getStoredSettings();
    const updatedSettings = { ...currentSettings, ...newSettings };

    await chrome.storage.sync.set({
      'ai_sheets_settings': updatedSettings
    });

    sendResponse({
      success: true,
      message: 'Settings saved successfully'
    });
  } catch (error) {
    sendResponse({
      success: false,
      error: error.message
    });
  }
}

// OAuth authentication
async function handleOAuthAuthentication(sendResponse) {
  try {
    const authUrl = `${API_BASE_URL}/api/auth/google?source=chrome_extension`;

    // Open OAuth flow in new tab
    const tab = await chrome.tabs.create({ url: authUrl });

    // Listen for the redirect with auth token
    const listener = (tabId, changeInfo, updatedTab) => {
      if (tabId === tab.id && changeInfo.url && changeInfo.url.includes('auth-success')) {
        // Extract token from URL
        const url = new URL(changeInfo.url);
        const token = url.searchParams.get('token');

        if (token) {
          // Save token
          chrome.storage.sync.set({
            'ai_sheets_auth_token': token
          });

          // Close auth tab
          chrome.tabs.remove(tabId);
          chrome.tabs.onUpdated.removeListener(listener);

          sendResponse({
            success: true,
            message: 'Authentication successful'
          });
        }
      }
    };

    chrome.tabs.onUpdated.addListener(listener);

  } catch (error) {
    sendResponse({
      success: false,
      error: error.message
    });
  }
}

// API connection test
async function testAPIConnection(sendResponse) {
  try {
    const settings = await getStoredSettings();

    const headers = {
      'Content-Type': 'application/json'
    };

    if (settings.authToken) {
      headers['Authorization'] = `Bearer ${settings.authToken}`;
    } else if (settings.apiKey) {
      headers['X-API-Key'] = settings.apiKey;
    } else {
      throw new Error('No authentication credentials configured');
    }

    const response = await fetch(`${API_BASE_URL}/api/test`, {
      method: 'POST',
      headers,
      body: JSON.stringify({
        function: 'test',
        input: 'connection test'
      })
    });

    if (response.ok) {
      sendResponse({
        success: true,
        message: 'Connection successful!'
      });
    } else {
      const errorData = await response.json();
      throw new Error(errorData.error || `Connection failed: ${response.status}`);
    }

  } catch (error) {
    sendResponse({
      success: false,
      error: error.message
    });
  }
}

// Usage statistics
async function updateUsageStats(functionName, credits) {
  try {
    const today = new Date().toDateString();
    const usageKey = `usage_${today}`;

    const result = await chrome.storage.local.get(usageKey);
    const todayUsage = result[usageKey] || {};

    if (!todayUsage[functionName]) {
      todayUsage[functionName] = { calls: 0, credits: 0 };
    }

    todayUsage[functionName].calls += 1;
    todayUsage[functionName].credits += credits;

    await chrome.storage.local.set({
      [usageKey]: todayUsage
    });

    // Clean up old usage data (keep last 30 days)
    cleanupOldUsageData();

  } catch (error) {
    console.error('Failed to update usage stats:', error);
  }
}

async function getUsageStats() {
  try {
    const today = new Date().toDateString();
    const usageKey = `usage_${today}`;

    const result = await chrome.storage.local.get(usageKey);
    const todayUsage = result[usageKey] || {};

    // Calculate totals
    let totalCalls = 0;
    let totalCredits = 0;

    Object.values(todayUsage).forEach(usage => {
      totalCalls += usage.calls;
      totalCredits += usage.credits;
    });

    return {
      today: {
        totalCalls,
        totalCredits,
        byFunction: todayUsage
      }
    };

  } catch (error) {
    console.error('Failed to get usage stats:', error);
    return { today: { totalCalls: 0, totalCredits: 0, byFunction: {} } };
  }
}

async function cleanupOldUsageData() {
  try {
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    const allData = await chrome.storage.local.get();
    const keysToRemove = [];

    Object.keys(allData).forEach(key => {
      if (key.startsWith('usage_')) {
        const dateString = key.replace('usage_', '');
        const date = new Date(dateString);

        if (date < thirtyDaysAgo) {
          keysToRemove.push(key);
        }
      }
    });

    if (keysToRemove.length > 0) {
      await chrome.storage.local.remove(keysToRemove);
    }

  } catch (error) {
    console.error('Failed to cleanup old usage data:', error);
  }
}

